import React from 'react';
import { Grid, Box, Typography } from '@mui/material';
import { keyframes } from '@emotion/react';

import html from '../../assets/html.png';
import css from '../../assets/css.png';
import javascripti from '../../assets/javascript.png';
import react from '../../assets/reactjs.png';
import ads from '../../assets/ads.png';
import content from '../../assets/content.png';
import seo from '../../assets/seo.png';

const Experince = () => {
    const stats = [
        { count: 1.5, label: 'Year of Experience', img: html },
        { count: 1.5, label: 'Year of Experience', img: css },
        { count: 10, label: 'Completed Projects', img: javascripti },
        { count: 5, label: 'Expert Employees', img: react },
        { count: 5, label: 'Expert Employees', img: ads },
        { count: 5, label: 'Expert Employees', img: content },
        { count: 5, label: 'Expert Employees', img: seo },
    ];

    // ✅ Define keyframes properly
    const scrollText = keyframes`
        0% { transform: translateX(0); }
        100% { transform: translateX(-50%); } /* only half since content is duplicated */
    `;

    const scrollImages = {
        display: 'flex',
        animation: `${scrollText} 30s linear infinite`, // faster scroll
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '60px',
        width: '200px',
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        maxHeight: '120px',
        borderRadius: '8px',
    };

    return (
        <Grid
            container
            sx={{
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 0, xs: 0 },
                mt: { xs: 8 },
                alignItems: 'stretch',
            }}
        >
            <Box sx={{ width: '90%' }}>
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '2.5rem', xs: '2rem' },
                        textAlign: 'center',
                        mb: 3,
                    }}
                >
                    My Skills
                </Typography>
            </Box>

            <Grid item xs={12} md={12}>
                <Box
                    sx={{
                        backgroundColor: '#fff',
                        py: 2,
                        px: 2,
                        mt: 2,
                        ml: 'auto',
                        mr: 'auto',
                        width: '95vw',
                        borderRadius: 10,
                        overflow: 'hidden',
                        height: '150px',
                    }}
                >
                    {/* ✅ Duplicate images for smooth infinite scroll */}
                    <Box sx={scrollImages}>
                        {[...stats, ...stats].map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}
                    </Box>
                </Box>
            </Grid>
        </Grid>
    );
};

export default Experince;
