import React, { useState } from 'react';
import { Grid, Typography, Button, Box, TextField, Alert, Snackbar ,ThemeProvider, createTheme} from '@mui/material';

const Connect = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
  });

  const [errors, setErrors] = useState({
    name: false,
    phone: false,
  });

  const [successSnackbar, setSuccessSnackbar] = useState(false);
  const [errorSnackbar, setErrorSnackbar] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const theme = createTheme({
    components: {
      MuiButton: {
        defaultProps: {
          disableRipple: false, // Ensure ripple is enabled
        },
        styleOverrides: {
          root: {
            '&:active': {
              '& .MuiTouchRipple-root': {
                color: '#FF4081', // Custom ripple color
              },
            },
          },
        },
      },
    },
  });

  const validateName = (name) => {
    const nameRegex = /^[a-zA-Z\s]+$/; // Allows letters and spaces only
    return nameRegex.test(name.trim());
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^[0-9]{10}$/; // Allows exactly 10 digits
    return phoneRegex.test(phone);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isNameValid = validateName(formData.name);
    const isPhoneValid = validatePhone(formData.phone);

    if (!isNameValid || !isPhoneValid) {
      setErrors({
        name: !isNameValid,
        phone: !isPhoneValid,
      });
      setErrorSnackbar(true); // Show error snackbar
      setSuccessSnackbar(false);
      return;
    }

    // Show success snackbar
    setErrors({ name: false, phone: false });
    setSuccessSnackbar(true);
    setErrorSnackbar(false);

    // Clear form data
    setFormData({ name: '', phone: '' });
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSuccessSnackbar(false);
    setErrorSnackbar(false);
  };

  return (
    <Grid
      container
      spacing={2}
      sx={{
        backgroundColor: '#efefef',
        padding: {xs:'30px 40px', md:'30px 80px'},
        alignItems: 'center',
      }}
    >
      {/* Title Section */}
      <Grid item xs={12}>
        <Typography
          variant="h3"
          sx={{
            fontWeight: 600,
            fontSize: { xs: '24px', md: '30px' },
            mt: 1,
            color: '#907394',
            textAlign: 'center',
            mb: 5,
          }}
        >
          Connect Now
        </Typography>
      </Grid>

      <Grid container sx={{ padding: 10, backgroundColor: 'white' }}>
        {/* Form Section */}
        <Grid
          item
          xs={12}
          md={8}
          sx={{
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            justifyContent: 'center',
            alignItems: 'center',
            gap: 2,
            mx: 'auto',
          }}
        >
          {/* Name Field */}
          <Box sx={{ width: { xs: '100%', md: '45%' } }}>
            <TextField
              fullWidth
              required
              id="name-field"
              label="Name"
              variant="standard"
              name="name"
              value={formData.name}
              onChange={handleChange}
              error={errors.name}
              helperText={errors.name ? 'Name must contain only letters and spaces' : ''}
            />
          </Box>

          {/* Phone Number Field */}
          <Box sx={{ width: { xs: '100%', md: '45%' } }}>
            <TextField
              fullWidth
              required
              id="phone-field"
              label="Phone Number"
              variant="standard"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              error={errors.phone}
              helperText={errors.phone ? 'Enter a valid 10-digit phone number' : ''}
            />
          </Box>
        </Grid>

        {/* Submit Button */}
        <Grid item xs={12} md={4} sx={{ textAlign: 'center' }}>
        <ThemeProvider theme={theme}>
      <Button
        variant="contained"
        color="primary"
        sx={{
          mt: { xs: 2, md: 2 },
          px: 4,
          borderRadius: '30px',
          backgroundColor: '#335064',
          '&:hover': {
            backgroundColor: '#705a6f',
          },
        }}
        onClick={handleSubmit}
      >
        Submit
      </Button>
    </ThemeProvider>
        </Grid>
      </Grid>

      {/* Success Snackbar */}
      <Snackbar
        open={successSnackbar}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity="success"
          sx={{
            textAlign: 'center',
            backgroundColor: 'white',
            color: 'green',
            borderRadius:'30px'
          }}
        >
          Form submitted successfully!
        </Alert>
      </Snackbar>

      {/* Error Snackbar */}
      <Snackbar
        open={errorSnackbar}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity="error"
          sx={{
            textAlign: 'center',
            backgroundColor: 'white',
            color: 'purple',
            borderRadius:'30px'
          }}
        >
          Please provide valid inputs for both Name and Phone Number.
        </Alert>
      </Snackbar>
    </Grid>
  );
};

export default Connect;
