import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 

const Banner = () => {
  // Slider settings
  const settings = {
    dots: false,
    infinite: true,
    speed: 500, // Smooth transition speed
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrow direction="next" />,
    prevArrow: <CustomArrow direction="prev" />,
  };

  // Array of images
  const images = [banner1, banner2, banner3];

  return (
    <div style={{ width: '100%' }}>
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index}>
            <img
              src={image}
            //   alt={Banner ${index + 1}}
              style={{
                width: '100%',
                height: 'auto', // Ensures full image is displayed
                maxHeight: '600px', // Limit height for consistent appearance
                objectFit: 'cover', // Fills the screen width
              }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

// Custom Arrow Component
const CustomArrow = ({ direction, ...props }) => {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: 'block',
        zIndex: 2,
        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background
        borderRadius: '50%', // Circular button
        padding: '10px',
        cursor: 'pointer', // Pointer on hover
        transition: 'transform 0.3s ease', // Smooth animation
        transform: 'scale(1)', // Initial scale
        position: 'absolute',
        top: '50%', // Center vertically
        transform: 'translateY(-50%)', // Adjust for centering
        ...(direction === 'next'
          ? { right: '10px' } // Position for the right arrow
          : { left: '10px' }), // Position for the left arrow
      }}
      onClick={onClick}
    //   onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.2)')} // Highlight on hover
    //   onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')} // Reset on hover out
    />
  );
};

export default Banner;
