import React, { useState } from 'react';
import { Grid, Box, Typography } from '@mui/material';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; // Import your image here
import banner2 from '../assets/cloud.png'

const cardsData = [
  {
    image: banner1,
    text: 'Card 1 Content: This is detailed information about the first card. The content should slide up when the user hovers over the "More About" block.',
    blockText: 'More about Card 1',
    bgColor: '#ff5722',
    blockColor: '#ffccbc',
  },
  {
    image: banner1,
    text: 'Card 2 Content: Detailed content for the second card. Hovering over the "More About" section will reveal this text.',
    blockText: 'More about Card 2',
    bgColor: '#2196f3',
    blockColor: '#bbdefb',
  },
  {
    image: banner1,
    text: 'Card 3 Content: Information for the third card. Hover to reveal more details below the "More About" section.',
    blockText: 'More about Card 3',
    bgColor: '#4caf50',
    blockColor: '#c8e6c9',
  },
];

const HoverCards = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  return (
    <Grid container spacing={4} sx={{ padding: '20px', backgroundImage:`url(${banner2})` }}>
        <Grid item  xs={12}>
<Typography>
    Academics
</Typography>
        </Grid>
      {cardsData.map((card, index) => (
        <Grid item xs={12} sm={6} md={4} key={index}>
          <Box
            sx={{
              position: 'relative',
              overflow: 'hidden',
              borderRadius: '8px',
              boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
            }}
          >
            {/* Card Image */}
            <Box
              component="img"
              src={card.image}
            //   alt={Card ${index + 1}}
              sx={{
                width: '100%',
                height: '250px', // Increased height for better image visibility
                objectFit: 'cover',
              }}
            />

            {/* Base Block ("More About" Section) */}
            <Box
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                width: '100%',
                height: '50px', // Default height
                backgroundColor: card.blockColor,
                color: '#000',
                padding: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontWeight: 'bold',
                fontSize: '14px',
                cursor: 'pointer',
                zIndex: 2,
                transition: 'all 0.3s ease-in-out',
                ...(hoveredCard === index && {
                  height: '80px', // Reduced height from the top when hovered
                  justifyContent: 'flex-start', // Move the text to the top
                  paddingTop: '20px', // Adjust padding to ensure text is correctly aligned
                }),
              }}
              onMouseEnter={() => setHoveredCard(index)} // Set hovered card
              onMouseLeave={() => setHoveredCard(null)} // Reset hovered card
            >
              {card.blockText}

              {/* Hidden Content inside the same block (visible on hover) */}
              <Box
                sx={{
                  position: 'absolute',
                  bottom: '10px', // Content starts below the "More About" text
                  left: 0,
                  textAlign:'center',
                  width: '100%',
                  color: '#fff',
                  fontSize: '16px',
                  fontWeight: 'normal',
                  padding: '10px',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  transition: 'all 0.3s ease-in-out',
                  opacity: hoveredCard === index ? 1 : 0, // Fade in content when hovered
                  visibility: hoveredCard === index ? 'visible' : 'hidden', // Make content visible on hover
                }}
              >
                <Typography>{card.text}</Typography>
              </Box>
            </Box>
          </Box>
        </Grid>
      ))}
    </Grid>
  );
};

export default HoverCards;
