import { Grid } from '@mui/material'
import React from 'react'
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';

const About = () => {
    return (
        <Grid container
            sx={{
                backgroundColor: '#f2f6fc',
                display: 'flex',
                padding:'80px 30px',
                // mt:-30
            }}>
            <Grid item xs={12} md={6}>
                About us
            </Grid>
            <Grid item xs={12} md={6} sx={{ alignItems: 'flex-end' }}>
                <img
                    src={banner1}
                    alt={banner1}
                    style={{ width: '80%', height: '100%' , borderRadius:'2%'}}  // Adjust image height for tablet
                />
            </Grid>

        </Grid>
    )
}

export default About
