import React from 'react';
import { Grid, Container, Box, Typography } from '@mui/material';
import portfolio1 from '../../assets/portfolio-1.png'; // Replace with actual image paths
import portfolio2 from '../../assets/portfolio-2.png';
import portfolio3 from '../../assets/portfolio-3.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: portfolio1,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: portfolio2,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: portfolio3,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 10s linear infinite',
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '20px',
        width: '200px',
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
    };

    const textStyle = {
        color: 'white',
        fontSize: '0.8rem',
        textAlign: 'center',
        marginTop: '10px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        <Container

        >
            <Grid
                container
                spacing={2} // Increased spacing between items
                justifyContent="center"
                alignItems="center"
            >
                <Box sx={{ width: '100%' }}>
                    {/* Heading */}
                    <Typography
                        variant="h2"
                        sx={{
                            color: 'white',
                            fontWeight: 500,
                            fontFamily: 'inherit',
                            fontSize: { md: '3rem', xs: '2rem' },
                            textAlign: 'center',
                        }}
                    >
                        My Skills
                    </Typography>


                </Box>
                <Grid item xs={12} maxWidth={false}
                    sx={{
                        backgroundColor: '#ed5345',
                        py: { xs: 2, md: 2 },
                        px: 2,
                        width: { md: '45vw', sm: '85vw', xs: '90vw' },
                        borderRadius: 2,
                        overflow: 'hidden', // To hide overflowed images
                    }}>
                    {/* Inline styles for auto-scrolling image container */}
                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {stats.map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                                <Typography variant="subtitle1" sx={textStyle}>
                                    {stat.label}
                                </Typography>
                            </Box>
                        ))}
                    </Box>
                </Grid>
            </Grid>
        </Container>
    );
};

export default Experince;
