import React, { useRef, useEffect, useState, } from 'react';
import { Typography, Grid, useMediaQuery, Container } from '@mui/material';
import LocalLibraryIcon from '@mui/icons-material/LocalLibrary';
import GroupIcon from '@mui/icons-material/Group';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import { Line } from '@react-three/drei';
import * as THREE from 'three';
import { useFrame } from '@react-three/fiber';

// Component to create fading, curved lines with shadow based on mouse movement


const Aboutdigital = () => {
    const isTablet = useMediaQuery('(min-width:601px) and (max-width:1024px)');

    const [completedProjects, setCompletedProjects] = useState(10);
    const [satisfiedCustomers, setSatisfiedCustomers] = useState(10);
    const [expertEmployees, setExpertEmployees] = useState(10);
    const [awardsWon, setAwardsWon] = useState(10);

    useEffect(() => {
        if (completedProjects < 900) {
            const interval = setInterval(() => {
                setCompletedProjects((prev) => Math.min(prev + 10, 1000));
            }, 30);
            return () => clearInterval(interval);
        }
    }, [completedProjects]);

    useEffect(() => {
        if (satisfiedCustomers < 60) {
            const interval = setInterval(() => {
                setSatisfiedCustomers((prev) => Math.min(prev + 10, 600));
            }, 30);
            return () => clearInterval(interval);
        }
    }, [satisfiedCustomers]);

    useEffect(() => {
        if (expertEmployees < 250 ) {
            const interval = setInterval(() => {
                setExpertEmployees((prev) => Math.min(prev + 1, 150));
            }, 10);
            return () => clearInterval(interval);
        }
    }, [expertEmployees]);


    const stats = [
        {
            icon: <LocalLibraryIcon sx={{ fontSize: { xs: 30, md: 50 }, color: '#00BFFF' }} />,
            count: completedProjects,
            label: 'Students',
        },
        {
            icon: <GroupIcon sx={{ fontSize: { xs: 30, md: 50 }, color: '#32CD32' }} />,
            count: satisfiedCustomers,
            label: 'Teaching Staff',
        },
        {
            icon: <BusinessCenterIcon sx={{ fontSize: { xs: 30, md: 50 }, color: '#FFA500' }} />,
            count: expertEmployees,
            label: 'Alumini Students',
        },

    ];

    return (
        <Container 
            maxWidth={false} 
            sx={{ 
                background: 'linear-gradient(to bottom,#ffffff, #e0f7fa)',
                // backgroundColor:'green',
                padding: { xs: '1px 0', md: '30px 0' , sm:'1px 0px'},
                width: '100vw', 
                marginLeft: 0, 
                marginRight: 0, 
                height:{ xs: '350px', md: '250px' , sm:'200px'},
                position: 'relative', // Make the container relative to hold absolute canvas
                zIndex: 1, // Keep content above canvas
            }}
        >


            <Grid
  container
  spacing={{ xs: 2, md: 4, sm: 2 }}
  sx={{
    justifyContent: { md: 'center', sm: 'left', xs: 'center' },
    flexDirection: 'row', // Row layout for all breakpoints
    flexWrap: 'wrap', // Ensures items wrap to next line if they overflow
  }}
>
  {stats.map((stat, index) => (
    <Grid
      item
      xs={12}
      sm={3} // Display in a row for both mobile (xs) and tablet (sm) with equal space
      md={3}
      sx={{
        textAlign: 'center',
        padding: { xs: '1px 0', md: '1px 0', sm: '1px 0px' },
        fontSize: isTablet ? '1.5rem' : 'initial',
        transform: isTablet ? 'scale(1.05)' : 'initial',
      }}
      key={index}
    >
      {stat.icon}
      <Typography
        variant="h4"
        sx={{
          fontWeight: 'bold',
          color: 'black',
          fontSize: { xs: '1.5rem', md: '2rem' },
        }}
      >
        {stat.count}+
      </Typography>
      <Typography
        variant="subtitle1"
        sx={{
          color: '#5cb8b5',
          fontWeight: 600,
          fontSize: { xs: '1rem', md: '1.5rem' },
        }}
      >
        {stat.label}
      </Typography>
    </Grid>
  ))}
</Grid>

        </Container>
    );
};

export default Aboutdigital;
