import { Grid, Typography, Button, Box } from '@mui/material';
import React from 'react';
import image from '../../assets/image.png';
import HomeRepairServiceIcon from '@mui/icons-material/HomeRepairService';

const Intro = () => {
    return (
        <Box
            sx={{
                position: "relative",
                height: { xs: "100vh", md: "90vh" },
                backgroundColor: "black",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                overflow: "hidden",
            }}
        >
            {/* Background Image */}
            <Box
                component="img"
                src={image}
                alt="Sahana NV"
                sx={{
                    height: "100%",
                    width: "100%",
                    objectFit: "cover",
                    position: "absolute",
                    top: 0,
                    left: 0,
                    zIndex: 1,
                }}
            />

            {/* Overlay Text */}
            <Box
                sx={{
                    position: "relative",
                    zIndex: 2,
                    color: "white",
                    textAlign: { xs: "center", md: "left" },
                    px: { xs: 2, md: 10 },
                }}
            >
                <Typography variant="h5" sx={{ color: "gray", mb: 1 }}>
                    Hello,
                </Typography>
                <Typography
                    variant="h2"
                    sx={{ fontWeight: "bold", lineHeight: 1.2 }}
                >
                    I'm <span style={{ color: "yellow" }}>Sahana NV</span><br />
                    Website Designer
                </Typography>
                <Typography sx={{ mt: 2, color: "lightgray", fontSize: "18px" }}>
                    I am a skilled web designer with experience in creating
                    visually appealing and user-friendly websites.
                </Typography>
                <Button
                    variant="contained"
                    sx={{
                        mt: 3,
                        color: "black",
                        backgroundColor: "white",
                        textTransform: "none",
                        gap: 1,
                        fontSize: "large",
                        borderRadius: "30px",
                        padding: "8px 20px",
                        "&:hover": {
                            backgroundColor: "yellow",
                            color: "black",
                        },
                    }}
                >
                    <HomeRepairServiceIcon />
                    Hire Me
                </Button>
            </Box>
        </Box>
    );
};

export default Intro;
