import React, { useState } from 'react';
import { Button, Grid, IconButton, Drawer } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import CloseIcon from '@mui/icons-material/Close';
import logo from '../../assets/logo1.png';
import message from '../../assets/contact.png';
import { Link } from 'react-scroll'; // ✅ use react-scroll

const Header = () => {
    const [mobileOpen, setMobileOpen] = useState(false);

    const links = [
        { label: 'Home', to: 'home' },
        { label: 'About', to: 'about' },
        { label: 'Experience', to: 'experience' },
        { label: 'Projects', to: 'projects' },
        { label: 'Contact', to: 'contact' },
    ];

    return (
        <div style={{ background: 'rgb(13, 1, 33)', color: 'white' }}>
            <Grid
                container
                alignItems="center"
                justifyContent="space-between"
                py="20px"
                sx={{
                    position: 'fixed',
                    top: 0,
                    px: { md: '70px', xs: '10px' },
                    zIndex: 1300,
                    backgroundColor: 'rgb(13, 1, 33)',
                }}
            >
                {/* Logo */}
                <Grid item xs={6} md={2}>
                    <Link to="home" smooth={true} duration={500}>
                        <img src={logo} alt="logo" style={{ height: '60px', cursor: 'pointer' }} />
                    </Link>
                </Grid>

                {/* Desktop Nav */}
                <Grid item xs={0} md={7} sx={{ display: { xs: 'none', md: 'flex' }, justifyContent: 'center', gap: 3 }}>
                    {links.map((link) => (
                        <Link key={link.to} to={link.to} smooth={true} duration={500} offset={-80} spy={true} activeClass="active-link">
                            <Button
                                sx={{
                                    color: 'white',
                                    textTransform: 'none',
                                    fontWeight: 'bold',
                                    fontSize: { xs: '0.9rem', md: '1.125rem' },
                                    '&:hover': { color: 'yellow' },
                                    '&.active-link': { color: 'yellow' },
                                }}
                            >
                                {link.label}
                            </Button>
                        </Link>
                    ))}
                </Grid>

                {/* Contact Button */}
                <Grid item md={3} sx={{ display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end' }}>
                    <Link to="contact" smooth={true} duration={500} offset={-80}>
                        <Button
                            variant="contained"
                            sx={{
                                background: 'white',
                                color: 'black',
                                fontWeight: 'bold',
                                display: 'flex',
                                alignItems: 'center',
                                gap: 1.5,
                                textTransform: 'none',
                            }}
                        >
                            <img src={message} alt="message icon" style={{ width: 20, height: 20 }} />
                            Contact Me
                        </Button>
                    </Link>
                </Grid>
            </Grid>
        </div>
    );
};

export default Header;
