import React from 'react';
import { Grid, Box } from '@mui/material';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 

// Custom Arrow Component (if you need arrows)
const CustomArrow = ({ direction, onClick }) => {
    return (
      <Box
        onClick={onClick}
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 2,
          backgroundColor: 'rgba(0, 0, 0, 0.5)', 
          borderRadius: '50%', 
          padding: '10px',
          cursor: 'pointer',
          position: 'absolute',
          top: '50%',
          transform: 'translateY(-50%)', 
          right: direction === 'next' ? '15px' : 'unset',
          left: direction === 'prev' ? '15px' : 'unset',
        }}
      >
        {direction === 'next' ? '>' : '<'}
      </Box>
    );
};

const Banner = () => {
  // Slider settings
  const settings = {
    dots: true,            // Enable dots for navigation
    infinite: true,
    speed: 800,            // Smooth transition speed
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,        // Enable auto-scroll
    autoplaySpeed: 3000,   // Set auto-scroll speed (3 seconds)
    nextArrow: <CustomArrow direction="next" />,
    prevArrow: <CustomArrow direction="prev" />,
  };

  // Array of images
  const images = [banner1, banner2, banner3];

  return (
    <Grid container sx={{ maxHeight: '60vh', overflow: 'hidden' }}>
      {/* <Slider {...settings}> */}
        {images.map((image, index) => (
          <Box key={index}>
            <img
              src={image}
              alt={`Slide ${index + 1}`}
              style={{
                width: '100%',
                height: '60vh',
                objectFit: 'cover',
              }}
            />
          </Box>
        ))}
      {/* </Slider> */}
    </Grid>
  );
};

export default Banner;
