import { Grid, Typography, Button, Box } from '@mui/material';
import React from 'react';
import image from '../../assets/image.png';
import HomeRepairServiceIcon from '@mui/icons-material/HomeRepairService';

const Intro = () => {
    return (
        <Grid
            container
            alignItems="center"
            sx={{
                backgroundColor: "rgb(30, 30, 30)",
                px: { md: 10, xs: 2 },
                py: { xs: 4, md: 0 },
                height: { xs: "100vh", md: "70vh" }, // ðŸ‘ˆ Control desktop height
            }}
        >
            {/* Text (desktop left, mobile overlay) */}
            <Grid
                item
                md={7}
                xs={12}
                sx={{
                    position: { xs: "absolute", md: "static" },
                    top: { xs: 0, md: "auto" },
                    left: { xs: 0, md: "auto" },
                    width: { xs: "100%", md: "auto" },
                    height: { xs: "100px", md: "auto" },
                    zIndex: { xs: 2, md: 1 },
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: { xs: "center", md: "flex-start" },
                    textAlign: { xs: "center", md: "left" },
                    px: { xs: 2, md: 10 },
                    background: { xs: "rgba(0,0,0,0.5)", md: "transparent" }, // dark overlay only on mobile
                }}
            >
                <Typography variant="h5" sx={{ color: "gray", mb: 1 }}>
                    Hello,
                </Typography>
                <Typography
                    variant="h2"
                    sx={{ fontWeight: "bold", lineHeight: 1.2 }}
                >
                    I'm <span style={{ color: "yellow" }}>Sahana NV</span><br />
                    Website Designer
                </Typography>
                <Typography sx={{ mt: 2, color: "lightgray", fontSize: "18px" }}>
                    I am a skilled web designer with experience in creating
                    visually appealing and user-friendly websites.
                </Typography>
                <Button
                    variant="contained"
                    sx={{
                        mt: 3,
                        color: "black",
                        backgroundColor: "white",
                        textTransform: "none",
                        gap: 1,
                        fontSize: "large",
                        borderRadius: "30px",
                        padding: "8px 20px",
                        "&:hover": {
                            backgroundColor: "yellow",
                            color: "black",
                        },
                    }}
                >
                    <HomeRepairServiceIcon />
                    Hire Me
                </Button>
            </Grid>

            {/* Image (background on mobile, side image on desktop) */}
            <Grid
                item
                md={5}
                xs={12}
                sx={{
                    position: "relative",
                    zIndex: { xs: 1, md: "auto" },
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                }}
            >
                <Box
                    component="img"
                    src={image}
                    alt="Sahana NV"
                    sx={{
                        width: "100%",
                        height: { xs: "100vh", md: "100%" }, // ðŸ‘ˆ keeps image smaller on desktop
                        maxHeight: { md: "70vh" },           // ðŸ‘ˆ restricts desktop image height
                        objectFit: { xs: "cover", md: "contain" },
                    }}
                />
            </Grid>
        </Grid>
    );
};

export default Intro;
