import React,{useState} from 'react';
import { Grid, Typography, Button, Box, TextField } from '@mui/material';

const Connect = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    phone: '',
    message: ''
});

const [errors, setErrors] = useState({
    email: false,
    phone: false,
    message: false
});

const [success, setSuccess] = useState(false);

const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
        ...prevData,
        [name]: value,
    }));
};

const validateEmail = (email) => {
    const emailRegex = /^[a-zA-Z0-9._%+-]+@gmail\.com$/;
    return emailRegex.test(email);
};

const validatePhone = (phone) => {
    const phoneRegex = /^[0-9]{10}$/;
    return phoneRegex.test(phone);
};
  const handleSubmit = (e) => {
    e.preventDefault();

    // const isEmailValid = validateEmail(formData.email);
    const isPhoneValid = validatePhone(formData.phone);
    const isMessageValid = formData.message.trim() !== '';

    if (  !isPhoneValid || !isMessageValid) {
        setErrors({
            // email: !isEmailValid,
            phone: !isPhoneValid,
            message: !isMessageValid
        });
        return;
    }

    const subject = encodeURIComponent(`Appointment Request: ${formData.subject}`);
    const body = encodeURIComponent(
        `Name: ${formData.name}\nEmail: ${formData.email}\nPhone: ${formData.phone}\n\nMessage:\n${formData.message}`
    );

    window.location.href = `mailto:gowthampatel024@gmail.com?subject=${subject}&body=${body}`;
    setFormData({
        name: '',
        email: '',
        subject: '',
        phone: '',
        message: ''
    });
    setErrors({
        email: false,
        phone: false,
        message: false
    });
    setSuccess(true);
};
  return (
    <Grid 
      container 
      spacing={2} 
      sx={{
        backgroundColor: '#efefef',
        padding: '30px 80px',
        alignItems: 'center',
// mt:5
      }}
    >
      {/* Title Section */}
      <Grid item xs={12}>
        <Typography 
          variant="h3" 
          sx={{
            fontWeight: 600,
            fontSize: { xs: '24px', md: '30px' },
            mt: 1, 
            color: '#907394',
            textAlign: 'center',
            mb:5
          }}
        >
          Connect Now
        </Typography>
      </Grid>
<Grid container sx={{padding:10, backgroundColor:'white'}}> 
      {/* Form Section */}
      <Grid 
        item 
        xs={12} 
        md={8} 
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          justifyContent: 'center',
          alignItems: 'center',
          gap: 2,
          mx: 'auto',
        }}
      >
        {/* Name Field */}
        <Box sx={{ width: { xs: '100%', md: '45%' } }}>
          <TextField
            fullWidth
            required
            id="name-field"
            label="Name"
            variant="standard"
          />
        </Box>

        {/* Phone Number Field */}
        <Box sx={{ width: { xs: '100%', md: '45%' } }}>
          <TextField
            fullWidth
            required
            id="phone-field"
            label="Phone Number"
            variant="standard"
          />
        </Box>
      </Grid>

      {/* Submit Button */}
      <Grid item xs={12} md={4} sx={{ textAlign: 'center' }}>
        <Button 
          variant="contained" 
          color="primary"
          sx={{
            mt: { xs: 2, md: 0 },
            px: 4,
            borderRadius:'30px',
            backgroundColor: '#335064',
            '&:hover': {
              backgroundColor: '#705a6f',
            },
          }}onClick={'handleSubmit'}
        >
          Submit
        </Button>
      </Grid>
      </Grid>
    </Grid>
  );
};

export default Connect;
