import { Container, Grid, Typography, Button } from '@mui/material'
import React from 'react'
import image from '../../assets/image.png'
import HomeRepairServiceIcon from '@mui/icons-material/HomeRepairService';

const Intro = () => {

    return (
        // <Container>
        <Grid
            container
            alignItems="center"
            spacing={2}
            sx={{ backgroundColor: 'black', px: { md: 10, xs: 2 }, py: 5 }}
        >
            {/* Left Content */}
            <Grid
                item
                md={7}
                xs={12}  // Full width on mobile
                sx={{ textAlign: { xs: "center", md: "justify" }, px: { md: 10, xs: 0 } }}
            >
                <Typography variant="h5" sx={{ color: "gray", mb: 1 }}>
                    Hello
                </Typography>
                <Typography
                    variant="h2"
                    sx={{ fontWeight: "bold", lineHeight: 1.2 }}
                >
                    I'm <span style={{ color: "yellow" }}>Sahana NV</span><br />
                    Website Designer
                </Typography>
                <Typography sx={{ mt: 2, color: "lightgray", fontSize: "18px" }}>
                    I design and build modern, responsive, and creative websites
                    that deliver great user experiences.
                </Typography>
                <Button
                    variant="contained"
                    sx={{
                        mt: 2,
                        color: "black",
                        backgroundColor: "grey",
                        textTransform: "none",
                        gap: 2,
                        fontSize: "large",
                        borderRadius: "30px",
                        boxShadow: 8,
                        "&:hover": {
                            backgroundColor: "lightgreen",
                            color: "black",
                        },
                    }}
                >
                    <HomeRepairServiceIcon />
                    Hire me
                </Button>
            </Grid>

            {/* Right Image */}
            <Grid
                item
                md={5}
                xs={12}  // Full width on mobile
                sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    overflow: "hidden",   // Prevents scrolling issues
                    mt: { xs: 3, md: 0 },
                }}
            >
                <img
                    src={image}
                    alt="Sahana NV"
                    style={{
                        maxWidth: "100%",
                        height: "auto",
                        objectFit: "contain",
                    }}
                />
            </Grid>
        </Grid>

        // </Container >
    )
}

export default Intro
