import React from 'react';
import { Grid, Box, Typography, Container } from '@mui/material';
import html from '../../assets/html.png'; // Replace with actual image paths
import css from '../../assets/css.png';
import javascripti from '../../assets/javascript.png';
import react from '../../assets/reactjs.png';
import ads from '../../assets/ads.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: html,
        },
        {
            count: 1.5,
            label: 'Year of Experience',
            img: css,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: javascripti,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: react,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: ads,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 30s linear infinite', // Slowed down scrolling
    };


    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        maxHeight: '100px', // Reduced height of images
        borderRadius: '8px',
    };

    const textStyle = {
        color: 'black',
        fontSize: '1rem', // Adjust font size
        textAlign: 'center',
        marginTop: '10px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        <Container
            maxWidth={false}
            sx={{
                backgroundColor: '#ed5345',
                py: { xs: 2, md: 2 },
                px: 2,
                width: { md: '90vw', sm: '85vw', xs: '90vw' },
                borderRadius: 2,
            }}
        >
            <Grid
                container
                spacing={2} // Increased spacing between items
                justifyContent="center"
                alignItems="center"
            >
                <Grid item xs={12} md={4} spacing={2}>
                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {stats.map((stat, index) => (
                            <Box key={index} sx={{ mt: 2, }}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                    gap={5}
                                />

                            </Box>
                        ))}
                    </Box>
                </Grid>
            </Grid>
        </Container>
    );
};

export default Experince;
