import React, { useState, useEffect } from 'react';
import { Grid } from '@mui/material';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import Slider from 'react-slick';
import styled from 'styled-components';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Array of banner images
const banners = [banner1, banner2, banner3]; // Replace with different images if needed

const Banner = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 5 seconds


  // Inline Styles
  const sliderContainerStyles = {
    position: 'relative',
    width: '100%',
    height: '400px', // Adjust height as needed
    overflow: 'hidden',
  };

  const StyledSlider = styled(Slider)({
    '& .slick-prev, & .slick-next': {
        zIndex: 1,
        width: 30,
        height: 30,
        '&:before': {
            fontSize: 30,
            color: 'black',
        },
    },
    '& .slick-prev': {
        left: -40,
        top: '50%',
        transform: 'translateY(-50%)',
    },
    '& .slick-next': {
        right: -30,
        top: '50%',
        transform: 'translateY(-50%)',
    },
});

const settings = {
    dots: false,
    arrows: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
        {
            breakpoint: 1024,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 1,
                infinite: true,
                dots: false,
            },
        },
        {
            breakpoint: 600,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
                dots: false,
            },
        },
    ],
};

  return (
    <Grid container style={sliderContainerStyles}>
      <Grid item xs={12}>
      <StyledSlider {...settings}>
                        {banners.map((banners, index) => (
                            <Grid key={index}>
                                 <img
          src={banners[currentIndex]}
          alt={`Banner ${currentIndex + 1}`}

        />
                            </Grid>
                        ))}
                    </StyledSlider>

      </Grid>


    </Grid>
  );
};

export default Banner;
