import React from 'react';
import { Grid, Box, Typography } from '@mui/material';
import html from '../../assets/html.png'; // Replace with actual image paths
import css from '../../assets/css.png';
import javascripti from '../../assets/javascript.png';
import react from '../../assets/reactjs.png';
import ads from '../../assets/ads.png';
import content from '../../assets/content.png';
import seo from '../../assets/seo.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: html,
        },
        {
            count: 1.5,
            label: 'Year of Experience',
            img: css,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: javascripti,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: react,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: ads,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: content,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: seo,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 60s linear infinite', // Adjusted to 60s for smoother scrolling
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '70px', // Increased space between images
        width: '200px', // Increased image width
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        maxHeight: '120px', // Adjusted height of images
        borderRadius: '8px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)', // Start from initial position
            },
            '100%': {
                transform: 'translateX(-100%)', // Move the entire set of images to the left
            },
        },
    };

    return (
        <Grid
            container
            sx={{
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 0, xs: 0 },
                mt: { xs: 8 },
                width: { md: '95vw', sm: '90vw', xs: '90vw' },
                overflow: 'hidden',
                alignItems: 'stretch',
            }}
        >
            <Box sx={{ width: '90%' }}>
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '2.5rem', xs: '2rem' },
                        textAlign: 'center',
                        mb: 3, // Added margin-bottom
                    }}
                >
                    My Skills
                </Typography>
            </Box>

            <Grid item xs={12} md={4}>
                <Box
                    sx={{
                        backgroundColor: '#fff',
                        py: { xs: 3, md: 0 },
                        px: 2,
                        mt: 2,
                        width: { md: '180vw', sm: '160vw', xs: '150vw' }, // Ensure the container is wide enough
                        borderRadius: 2,
                        overflow: 'hidden',
                        height: '150px', // Adjusted height for the images
                    }}
                >
                    {/* Inline styles for auto-scrolling image container */}
                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {/* First set of images */}
                        {stats.map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}

                        {/* Duplicate set of images for continuous scrolling */}
                        {stats.map((stat, index) => (
                            <Box key={index + stats.length} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}
                    </Box>
                </Box>
            </Grid>
        </Grid>
    );
};

export default Experince;
