import React, { useEffect, useState } from 'react';
import { Typography, Grid, useMediaQuery, Container } from '@mui/material';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import GroupIcon from '@mui/icons-material/Group';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';

const Experince = () => {
    const isTablet = useMediaQuery('(min-width:601px) and (max-width:1024px)');

    const [completedProjects, setCompletedProjects] = useState(10);
    const [satisfiedCustomers, setSatisfiedCustomers] = useState(10);
    const [expertEmployees, setExpertEmployees] = useState(10);
    const [awardsWon, setAwardsWon] = useState(10);

    useEffect(() => {
        const updateCount = (setter, max, step, delay) => {
            const interval = setInterval(() => {
                setter((prev) => Math.min(prev + step, max));
            }, delay);
            return () => clearInterval(interval);
        };

        const intervals = [
            updateCount(setCompletedProjects, 1000, 10, 30),
            updateCount(setSatisfiedCustomers, 600, 10, 30),
            updateCount(setExpertEmployees, 50, 1, 100),
            updateCount(setAwardsWon, 20, 1, 100),
        ];

        return () => intervals.forEach(clearInterval);
    }, []);

    const stats = [
        {
            icon: <CheckCircleOutlineIcon sx={{ fontSize: { xs: 30, md: 30 }, color: 'lightgrey' }} />,
            count: completedProjects,
            label: 'Completed Projects',
        },
        {
            icon: <GroupIcon sx={{ fontSize: { xs: 30, md: 30 }, color: 'lightgrey' }} />,
            count: satisfiedCustomers,
            label: 'Satisfied Customers',
        },
        {
            icon: <BusinessCenterIcon sx={{ fontSize: { xs: 30, md: 30 }, color: 'lightgrey' }} />,
            count: expertEmployees,
            label: 'Expert Employees',
        },
        {
            icon: <EmojiEventsIcon sx={{ fontSize: { xs: 30, md: 30 }, color: 'lightgrey' }} />,
            count: awardsWon,
            label: 'Awards Won',
        },
    ];

    return (
        <Container
            maxWidth={false}
            sx={{
                backgroundColor: 'black',
                py: { xs: 2, md: 2 },
                width: '90vw',
            }}
        >
            <Grid
                container
                spacing={{ xs: 2, md: 4 }}
                justifyContent="center"
                alignItems="center"
            >
                {stats.map((stat, index) => (
                    <Grid
                        item
                        xs={6}
                        sm={3}
                        md={3}
                        key={index}
                        sx={{
                            textAlign: 'center',
                            fontSize: isTablet ? '1.5rem' : 'initial',
                            transform: isTablet ? 'scale(1.05)' : 'initial',
                        }}
                    >

                        <Typography
                            variant="h4"
                            sx={{
                                fontWeight: 'bold',
                                color: 'lightgrey',
                                fontSize: { xs: '1rem', md: '1.5rem' },
                            }}
                        >
                            {stat.count}+
                        </Typography>
                        <Typography
                            variant="subtitle1"
                            sx={{
                                color: 'lightgrey',
                                fontSize: { xs: '0.8rem', md: '1.0rem' },
                            }}
                        >
                            {stat.label}
                        </Typography>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Experince;
