import React from 'react';
import { Grid, Typography, Button, Box, TextField } from '@mui/material';

const Connect = () => {
  return (
    <Grid 
      container 
      spacing={2} 
      sx={{
        backgroundColor: '#efefef',
        padding: '30px 80px',
        alignItems: 'center',
mt:5
      }}
    >
      {/* Title Section */}
      <Grid item xs={12}>
        <Typography 
          variant="h3" 
          sx={{
            fontWeight: 600,
            fontSize: { xs: '24px', md: '30px' },
            mt: 1, 
            color: '#907394',
            textAlign: 'center',
            mb:5
          }}
        >
          Connect Now
        </Typography>
      </Grid>
<Grid container sx={{padding:10, backgroundColor:'white'}}> 
      {/* Form Section */}
      <Grid 
        item 
        xs={12} 
        md={8} 
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          justifyContent: 'center',
          alignItems: 'center',
          gap: 2,
          mx: 'auto',
        }}
      >
        {/* Name Field */}
        <Box sx={{ width: { xs: '100%', md: '45%' } }}>
          <TextField
            fullWidth
            required
            id="name-field"
            label="Name"
            variant="standard"
          />
        </Box>

        {/* Phone Number Field */}
        <Box sx={{ width: { xs: '100%', md: '45%' } }}>
          <TextField
            fullWidth
            required
            id="phone-field"
            label="Phone Number"
            variant="standard"
          />
        </Box>
      </Grid>

      {/* Submit Button */}
      <Grid item xs={12} md={4} sx={{ textAlign: 'center' }}>
        <Button 
          variant="contained" 
          color="primary"
          sx={{
            mt: { xs: 2, md: 0 },
            px: 4,
            borderRadius:'30px',
            backgroundColor: '#335064',
            '&:hover': {
              backgroundColor: '#705a6f',
            },
          }}
        >
          Submit
        </Button>
      </Grid>
      </Grid>
    </Grid>
  );
};

export default Connect;
