import React from 'react';
import { Typography, Grid, Box, Button } from '@mui/material';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../assets/banner.jpg';
import banner2 from '../assets/banner.jpg';
import banner3 from '../assets/banner.jpg';
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';

// Custom Arrow Component
const CustomArrow = ({ direction, onClick }) => {
  return (
    <Box
      onClick={onClick}
      style={{
        display: 'flex',
        zIndex: 1,
        borderRadius: '50%',
        padding: '10px',
        cursor: 'pointer',
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        right: direction === 'next' ? '15px' : 'unset',
        left: direction === 'prev' ? '15px' : 'unset',
      }}
    >
      <span
        style={{
          fontSize: '35px',
          color: 'blue',
          userSelect: 'none',
        }}
      >
        {direction === 'next' ? (
          <ArrowCircleRightIcon sx={{ color: 'grey', fontSize: 40 }} />
        ) : (
          <ArrowCircleLeftIcon sx={{ color: 'grey', fontSize: 40 }} />
        )}
      </span>
    </Box>
  );
};

const carouselItems = [
  {
    image: banner1,
    title: "Empowering Future Leaders in Management and Science",
    // description: "",
  },
  {
    image: banner2,
    title: "Explore a wide range of courses",
    // description: "Explore a wide range of courses ",
  },
  {
    image: banner3,
    title: "A legacy of quality education and skill development",
    // description: "A legacy of quality education and skill development",
  },
];

const Banner = () => {
  // Slider settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrow direction="next" />,
    prevArrow: <CustomArrow direction="prev" />,
  };

  return (
    <Grid style={{ width: '100%' }}>
      <Slider {...settings}>
        {carouselItems.map((item, index) => (
          <Box key={index} sx={{ position: 'relative' }}>
            {/* Image */}
            <img
              src={item.image}
              alt={`Banner ${index + 1}`}
              style={{
                width: '100%',
                // height: '800px',
                minHeight: '600px',
                objectFit: 'cover',
              }}
            />

            {/* Text Overlay */}
            <Box
              sx={{
                position: 'absolute',
                top: '50%',
                left: '50%',
                boxSizing: 'inherit',
                transform: 'translate(-50%, -50%)',
                textAlign: 'center',
                color: 'black',
                backgroundColor: 'rgba(135, 206, 245, 0.4)', 
                padding: '20px',
                borderRadius: '8px',
              }}
            >
              <Typography variant="h3" sx={{ fontWeight: 400, mb: 2 , fontSize:{md:'30px', sm:'30px', xs:'15px'}}}>
                {item.title}
              </Typography>
              <Typography variant="h4" sx={{ mb: 3 }}>
                {item.description}
              </Typography>

            </Box>
          </Box>
        ))}
      </Slider>
    </Grid>
  );
};

export default Banner;
