import React, {useState} from 'react';
import { Grid, Card, CardContent, Typography ,Box, CardMedia} from '@mui/material';
import princy from '../assets/cards image/princy.png';
import image2 from '../assets/cards image/princy.png'; // Add more image imports
import image3 from '../assets/cards image/princy.png';

const imageList = [princy, image2, image3]; // Array of image sources
const names = ["Ramanuj", "Shankari", "Kumar"]; // Array of names

const Cards = () => {
    const [showMore, setShowMore] = useState(false);

    const toggleShowMore = () => {
        setShowMore(!showMore);
    };

  return (
    <Grid
      container
      spacing={2}
      sx={{ background: 'linear-gradient(to top,#ffffff, #D9EEE1)', height: '80vh', padding: 2 }}
    >
      {imageList.map((image, index) => (
        <Grid item xs={12} sm={4} key={index}>
          <Card
            sx={{
              background: 'yellow',
              height: '80%',
              width:'100%',
            //   display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <CardMedia>
            <img
              src={image}
              alt={`Image ${index + 1}`}
              style={{ height: '80%', width: '100%', objectFit: 'cover' }}
            />
            </CardMedia>

          <CardContent>
            <Typography variant="h6">Name: {index.name}</Typography>
          </CardContent>
          </Card>
          {showMore && (
                        // <Box sx={{ marginTop: '20px' }}>
                        //     <Typography variant="body1" sx={{ marginTop: '15px', maxWidth: '600px', textAlign: 'justify' }}>
                        //         With a focus on digital marketing we empower our clients to reach their goals and enhance their online visibility. Our team of knowledgeable professionals uses state-of-the-art technologies to boost digital presence and propel business growth.
                        //     </Typography>
                        // </Box>
                        <Card sx={{backgroundColor:'yellowgreen'}}>
            </Card>
                    )}

        </Grid>
      ))}
    </Grid>
  );
};

export default Cards;
