import { Typography, Box, Button, useMediaQuery, useTheme } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Slider from 'react-slick';
import React from 'react';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import ArrowCircleLeftRoundedIcon from '@mui/icons-material/ArrowCircleLeftRounded';
import ArrowCircleRightRoundedIcon from '@mui/icons-material/ArrowCircleRightRounded';

const slidesData = [
  {
    title: "Digital Marketing Courses - Strategy and Resources",
    image: banner1,
    buttonText: "Explore More",
    path: '/digital-marketing',
  },
  {
    title: "Transformative Ideas for Growth and Success",
    image: banner1,
    buttonText: "Discover More",
    path: '/services-page',
  },
];

const Arrow = ({ className, style, onClick, direction }) => {
  return (
    <Box
      className={className}
      sx={{
        ...style,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
        cursor: 'pointer',
        zIndex: 1,
        width: '40px',
        height: '40px',
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        [direction === 'left' ? 'left' : 'right']: ['10px', '30px', '70px'], // Responsive adjustment
      }}
      onClick={onClick}
    >
      {direction === 'left' ? (
        <ArrowCircleLeftRoundedIcon />
      ) : (
        <ArrowCircleRightRoundedIcon />
      )}
    </Box>
  );
};

const Banner = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <Arrow direction="right" />,
    prevArrow: <Arrow direction="left" />,
    customPaging: (i) => (
      <Box sx={{}} />
    ),
  };

  return (
    <Box
      sx={{
        pl: { md: 2, xs: 2 },
        width: '100%',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '550px',
      }}
    >
      <Slider {...sliderSettings}>
        {slidesData.map((slide, index) => (
          <Box key={index}>
            <Box
              sx={{
                display: 'flex',
                mt: 5,
                boxDirection: 'flow',
                alignItems: 'center',
                justifyContent: 'center',
                padding: '60px',
                flexDirection: isMobile ? 'column' : 'row', // Stack content on mobile
              }}
            >
              <Box
                sx={{
                  width: isMobile ? '100%' : '50%',
                  textAlign: isMobile ? 'center' : 'left',
                  padding: '10px',
                }}
              >
                <Typography
                  variant="h1"
                  sx={{
                    fontWeight: 'bold',
                    marginBottom: 2,
                    marginRight: 0,
                    color: 'white',
                  }}
                >
                  {slide.title}
                </Typography>

                <Button
                  variant="contained"
                  sx={{
                    borderRadius: '40px',
                    backgroundColor: 'linear-gradient(90deg, #205deb 0%, #3a93f3 50%, #53c7fa 100%)',
                    color: 'white',
                    padding: '10px 30px',
                    mb: '0',
                  }}
                  onClick={() => navigate(slide.path)}
                >
                  {slide.buttonText}
                </Button>
              </Box>

              <Box
                sx={{
                  width: isMobile ? '100%' : '40%',
                  textAlign: 'right',
                  padding: '10px',
                }}
              >
                <img
                  src={slide.image}
                  alt={slide.title}
                  style={{
                    width: '90%',
                    height: 'auto', // Make image height flexible
                    borderRadius: '20px',
                  }}
                />
              </Box>
            </Box>
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default Banner;
