import React from 'react';
import cloud from '../assets/cloud.png'

const CloudBackground = () => {
  return (
    <div
      style={{
        width: '100%',
        height: '300px',
        backgroundImage: `url(${cloud})`, // Sky gradient
        position: 'relative',
        overflow: 'hidden',
      }}
    >
      {/* Top Clouds */}
      <div
        style={{
          position: 'absolute',
          top: '0px',
          width: '100%',
          height: '150px',
          background: 'radial-gradient(circle at 50% 50%, #fff, transparent)',
          filter: 'blur(10px)',
        }}
      ></div>

      {/* Middle Cloud Layer */}
      <div
        style={{
          position: 'absolute',
          top: '50px',
          width: '100%',
          height: '200px',
          background:
            'radial-gradient(circle at 30% 30%, #fff, transparent), radial-gradient(circle at 70% 30%, #fff, transparent)',
          filter: 'blur(15px)',
        }}
      ></div>

      {/* Bottom Clouds */}
      <div
        style={{
          position: 'absolute',
          bottom: '0px',
          width: '100%',
          height: '150px',
          background:
            'radial-gradient(circle at 20% 70%, #fff, transparent), radial-gradient(circle at 80% 70%, #fff, transparent)',
          filter: 'blur(15px)',
        }}
      ></div>
    </div>
  );
};

export default CloudBackground;
