import { Grid, Typography, Box, Card, CardMedia } from '@mui/material';
import React from 'react';
import portfolio1 from '../../assets/portfolio-1.png';
import portfolio2 from '../../assets/portfolio-2.png';
import portfolio3 from '../../assets/portfolio-3.png';
import portfolio4 from '../../assets/portfolio-4.png'
import portfolio5 from '../../assets/portfolio-5.png'
import portfolio6 from '../../assets/portfolio-6.png'

const skillsData = [
    {
        id: 1,
        img: portfolio1,
    },
    {
        id: 2,
        img: portfolio2,

    },
    {
        id: 3,
        img: portfolio3,

    },
    {
        id: 4,
        img: portfolio4,

    },
    {
        id: 5,
        img: portfolio5,

    },
    {
        id: 6,
        img: portfolio6,

    },
];

const Works = () => {
    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(30, 30, 30)',
                px: { md: 10, xs: 3 },
                py: { md: 6, xs: 4 },
                mt: { md: -8, xs: 3 },
                minHeight: { xs: '100vh', md: '90vh' },
                overflow: 'hidden',
                justifyContent: 'center',
            }}
        >
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    My Portofolio
                </Typography>

                <Typography
                    variant="body2"
                    sx={{
                        color: 'lightgray',
                        fontFamily: 'inherit',
                        fontSize: { md: '1.2rem', xs: '0.9rem' },
                        lineHeight: 1.5,
                        textAlign: 'center',
                        mt: 2,
                        mb: 5,
                    }}
                >
                    I am a skilled and passionate web designer with experience in creating visually appealing and user-friendly websites. I have a strong understanding of design and a keen eye for detail. I am proficient in HTML, CSS, JavaScript, and React.js. Along with my technical expertise, I also have knowledge of digital marketing, including SEO, Google Ads, and content strategies, which helps me create websites that not only look great but also perform effectively in reaching the right audience.
                </Typography>

                {/* Skills List Style */}
                <Grid container spacing={3} justifyContent="center">
                    {skillsData.map((skill) => (
                        <Grid item xs={12} md={4} key={skill.id}>
                            <Card
                                sx={{
                                    background: 'lightgray',
                                    color: '#1e1e1e',
                                    height: '300px', // smaller height for better visuals
                                    borderRadius: 2,
                                    overflow: 'hidden',
                                    animation: 'zoomInOut 5s ease-in-out infinite', // animation added
                                    '@keyframes zoomInOut': {
                                        '0%': {
                                            transform: 'scale(1)',
                                        },
                                        '50%': {
                                            transform: 'scale(1.05)',
                                        },
                                        '100%': {
                                            transform: 'scale(1)',
                                        },
                                    },
                                }}
                            >
                                <CardMedia
                                    component="img"
                                    src={skill.img}
                                    sx={{
                                        width: '100%',
                                        height: '100%',
                                        objectFit: 'cover', // ensures image fills the card
                                    }} />

                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Box>
        </Grid>
    );
};

export default Works;
