import React, { useState, useEffect } from 'react';
import { Grid } from '@mui/material';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';

// Array of banner images
const banners = [banner1, banner2, banner3]; // Replace with different images if needed

const Banner = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, []);

  // Next Slide
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % banners.length);
  };

  // Previous Slide
  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? banners.length - 1 : prevIndex - 1
    );
  };

  // Inline Styles
  const sliderContainerStyles = {
    position: 'relative',
    width: '100%',
    height: '400px', // Adjust height as needed
    overflow: 'hidden',
  };

  const slideImageStyles = {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    transition: 'transform 0.5s ease-in-out',
  };

  const arrowStyles = {
    position: 'absolute',
    top: '50%',
    transform: 'translateY(-50%)',
    fontSize: '30px',
    color: '#fff',
    cursor: 'pointer',
    zIndex: 1, // Ensure arrows are on top of the image
    userSelect: 'none',
  };

  const leftArrowStyles = {
    ...arrowStyles,
    left: '20px',
  };

  const rightArrowStyles = {
    ...arrowStyles,
    right: '20px',
  };

  return (
    <Grid container style={sliderContainerStyles}>
      <Grid item xs={12}>
        <img
          src={banners[currentIndex]}
          alt={`Banner ${currentIndex + 1}`}
          style={slideImageStyles}
        />
      </Grid>

      {/* Left Arrow */}
      <div style={leftArrowStyles} onClick={prevSlide}>
        ❮
      </div>

      {/* Right Arrow */}
      <div style={rightArrowStyles} onClick={nextSlide}>
        ❯
      </div>
    </Grid>
  );
};

export default Banner;
