import { Grid, Typography, Box, Card, CardContent, CardMedia } from '@mui/material';
import React from 'react';
import ui from '../../assets/ui-design.png';
import web from '../../assets/website-design.png';
import graphic from '../../assets/app-design.png';

const skillsData = [
  {
    id: 1,
    title: "UI/UX Design",
    img: ui,
    desc: "Designing intuitive and engaging user interfaces with seamless experiences.",
  },
  {
    id: 2,
    title: "Web Development",
    img: web,
    desc: "Building responsive, dynamic, and high-performing web applications.",
  },
  {
    id: 3,
    title: "Graphic Design",
    img: graphic,
    desc: "Creating visually appealing graphics that enhance brand identity.",
  },
];

const Skills = () => {
  return (
    <Grid
      container
      sx={{
        position: 'relative',
        backgroundColor: 'rgb(30, 30, 30)',
        px: { md: 10, xs: 3 },
        py: { md: 6, xs: 4 },
        mt: { md: -2, xs: 3 },
        minHeight: { xs: '100vh', md: '90vh' },
        overflow: 'hidden',
        textAlign: 'center',
        justifyContent: 'center',
      }}
    >
      <Box sx={{ width: '100%' }}>
        {/* Heading */}
        <Typography
          variant="h2"
          sx={{
            color: 'white',
            fontWeight: 600,
            fontFamily: 'inherit',
            fontSize: { md: '3rem', xs: '2rem' },
            mb: 0,
          }}
        >
          What I Do
        </Typography>

        <Typography
          variant="body2"
          sx={{
            color: 'lightgray',
            fontFamily: 'inherit',
            fontSize: { md: '1.5rem', xs: '0.9rem' },
            lineHeight: 1.5,
            mt: 2,
            mb: 5,
          }}
        >
          I am a skilled and passionate web designer with experience in
          creating visually appealing and user-friendly websites. I have a strong
          understanding of design and a keen eye for detail. I am proficient in HTML,
          CSS, JavaScript, and React.js, as well as design software such as Adobe Photoshop.
        </Typography>

        {/* Skills Cards */}
        <Grid container spacing={4} justifyContent="center">
          {skillsData.map((skill) => (
            <Grid item xs={12} sm={6} md={6} key={skill.id}>
              <Card
                sx={{
                  background: 'white',
                  color: 'white',
                  borderRadius: 3,
                  boxShadow: 4,
                  height: '100%',
                  transition: '0.3s',
                  "&:hover": { transform: 'translateY(-8px)', boxShadow: 8 },
                  textAlign: 'left',
                  p: 2,
                }}
              >
                {/* Row: Image + Title */}
                <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
                  <CardMedia
                    component="img"
                    src={skill.img}
                    alt={skill.title}
                    sx={{
                      width: 50,
                      height: 80,
                      objectFit: "contain",
                    }}
                  />
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: 600,
                      fontSize: { md: "1.2rem", xs: "1rem" },
                    }}
                  >
                    {skill.title}
                  </Typography>
                </Box>

                {/* Description Line */}
                <CardContent sx={{ px: 0, pt: 2 }}>
                  <Typography
                    variant="body2"
                    sx={{
                      color: "lightgray",
                      fontSize: { md: "1rem", xs: "0.85rem" },
                      lineHeight: 1.5,
                    }}
                  >
                    {skill.desc}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Grid>
  );
};

export default Skills;
