import { Grid, Typography } from '@mui/material'
import React from 'react'
import { motion } from 'framer-motion'
import banner from '../../images/Banner.png'

const borderAnimation = {
  background: [
    'linear-gradient(45deg, #00ffc8, #007bff)',
    'linear-gradient(45deg, #007bff, #4b0082)',
    'linear-gradient(45deg, #4b0082, #00ffc8)',
  ],
  transition: {
    duration: 5,
    repeat: Infinity,
    ease: 'linear'
  }
}

const AcademicsPage = () => {
  return (
    <Grid container sx={{ height: '60vh', p: '20px 30px' }}>
      <Grid item xs={12} md={7}>
        <Typography variant='h2' sx={{ fontSize: '30px' }}>
          Benefits of Choosing the PU Science Stream
        </Typography>
      </Grid>
      <Grid item xs={12} md={5} sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'relative' }}>
        {/* Animated Border Background */}
        <motion.div
          style={{
            width: '90%',
            height: '90%',
            position: 'absolute',
            borderRadius: '10px',
            padding: '8px',
            backgroundSize: '200% 200%',
            // filter: 'blur(10px)', // Soft glow effect
            zIndex: 0, // Keeps background behind the image
          }}
          animate={borderAnimation}
        />

        {/* Image Container */}
        <div style={{
          width: '90%',
          height: '90%',
          position: 'relative',
          // borderRadius: '10px',
          overflow: 'hidden', // Ensures border effect stays behind image
          zIndex: 1, // Keeps image above the animated background
        }}>
          <img src={banner} style={{ width: '100%', height: '100%', borderRadius: '5px' }} />
        </div>
      </Grid>
    </Grid>
  )
}

export default AcademicsPage
