import { Grid, Typography, Button, Box } from '@mui/material';
import React from 'react';
import image from '../../assets/logo12.jpg';
import HomeRepairServiceIcon from '@mui/icons-material/HomeRepairService';

const Intro = () => {
    return (
        <Grid
            container
            sx={{
                backgroundColor: 'rgb(30, 30, 30)',
                mt: { md: 4, xs: 0 },
                height: { xs: 'auto', md: '90vh' },
                alignItems: 'center',
                overflow: 'hidden',
                position: 'relative',
            }}
        >
            {/* Left Text Content */}
            <Grid
                item
                xs={12}
                md={6}
                sx={{
                    color: 'rgba(255, 255, 255, 0.85)',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    px: { xs: 3, md: 10 },
                    py: { xs: 6, md: 0 },
                    zIndex: 2,
                    textAlign: 'left',
                }}
            >
                <Typography variant="h5" sx={{ color: 'grey', mb: 1 }}>
                    Hello,
                </Typography>

                <Typography
                    variant="h2"
                    sx={{
                        fontWeight: 'bold',
                        lineHeight: 1.2,
                        fontSize: { xs: '2rem', md: '3.75rem' },
                    }}
                >
                    I'm <span style={{ color: 'yellow' }}>Sahana NV</span>
                    <br />
                    Website Designer
                </Typography>

                <Typography
                    sx={{
                        mt: 2,
                        color: 'lightgray',
                        fontSize: { xs: '1rem', md: '1.125rem' },
                        maxWidth: { md: '500px' },
                    }}
                >
                    I am a skilled web designer with experience in creating
                    visually appealing and user-friendly websites.
                </Typography>

                <Button
                    variant="contained"
                    sx={{
                        mt: 6,
                        color: 'black',
                        backgroundColor: 'white',
                        textTransform: 'none',
                        gap: 1,
                        fontSize: { xs: '1rem', md: 'large' },
                        borderRadius: '30px',
                        padding: '8px 20px',
                        '&:hover': {
                            backgroundColor: 'yellow',
                            color: 'black',
                        },
                        alignSelf: { xs: 'flex-start', md: 'flex-start' },
                    }}
                >
                    <HomeRepairServiceIcon />
                    Hire Me
                </Button>
            </Grid>

            {/* Right Image */}
            <Grid
                item
                xs={12}
                md={6}
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    py: { xs: 4, md: 0 },
                    px: { xs: 3, md: 0 },
                }}
            >
                <Box
                    component="img"
                    src={image}
                    alt="Sahana NV"
                    sx={{
                        width: { xs: '100%', sm: '80%', md: 450 },
                        height: { xs: 'auto', md: 500 },
                        objectFit: 'cover',
                        filter: { xs: 'brightness(0.95)', md: 'none' },
                        borderRadius: { xs: '10px', md: '50%' },
                    }}
                />
            </Grid>
        </Grid>
    );
};

export default Intro;
