import React, { useEffect, useRef, useState } from 'react';
import {
    Box,
    Card,
    CardContent,
    Typography,
    Stack,
    Container,
} from '@mui/material';

/* ================================
   PDFViewer Component (auto-scroll on hover)
================================ */
function PDFViewer({ url, height = 400 }) {
    const scrollRef = useRef(null);
    const scrollIntervalRef = useRef(null);
    const [isHovering, setIsHovering] = useState(false);

    useEffect(() => {
        const container = scrollRef.current;
        if (!container) return;

        if (isHovering) {
            scrollIntervalRef.current = setInterval(() => {
                // Scroll down 1px each step
                if (container.scrollTop + container.clientHeight >= container.scrollHeight) {
                    container.scrollTop = 0; // Loop back to top
                } else {
                    container.scrollTop += 1;
                }
            }, 30); // Adjust scroll speed here
        } else {
            clearInterval(scrollIntervalRef.current);
        }

        // Cleanup on unmount or when hover changes
        return () => clearInterval(scrollIntervalRef.current);
    }, [isHovering]);

    if (!url) {
        return (
            <Box
                sx={{
                    height,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    bgcolor: '#f5f5f5',
                }}
            >
                <Typography variant="body2" color="text.secondary">
                    No PDF provided
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            ref={scrollRef}
            sx={{
                height,
                overflowY: 'scroll',
                borderRadius: 2,
                scrollbarWidth: 'none',
                '&::-webkit-scrollbar': { display: 'none' },
                cursor: 'pointer',
                boxShadow: 3,
            }}
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
        >
            <iframe
                src={url}
                width="100%"
                height="1200px" // large height for scroll inside container
                frameBorder="0"
                title="pdf-viewer"
                style={{
                    pointerEvents: 'none', // disables interaction inside iframe
                    borderRadius: 8,
                }}
            />
        </Box>
    );
}

/* ================================
   Main Component: 3 PDF Cards with hover auto-scroll
================================ */
export default function PPTCards() {
    // Replace these URLs with your PDFs in /public folder
    const pdfUrls = [
        '/presentation1.pdf',
        '/presentation2.pdf',
        '/presentation3.pdf',
    ];

    return (
        <Container sx={{ py: 4 }}>
            <Typography variant="h5" sx={{ mb: 3, textAlign: 'center' }}>
                3 PDF Cards (Auto-scroll on hover)
            </Typography>

            <Stack direction={{ xs: 'column', md: 'row' }} spacing={3}>
                {pdfUrls.map((url, idx) => (
                    <Card key={idx} sx={{ flex: 1 }}>
                        <PDFViewer url={url} height={400} />
                        <CardContent>
                            <Typography variant="h6">{`Presentation ${idx + 1}`}</Typography>
                            <Typography variant="body2" color="text.secondary">
                                Hover to auto-scroll PDF content.
                            </Typography>
                        </CardContent>
                    </Card>
                ))}
            </Stack>
        </Container>
    );
}
