import React from 'react';
import { Grid, Box, Typography } from '@mui/material';
import html from '../../assets/html.png'; // Replace with actual image paths
import css from '../../assets/css.png';
import javascripti from '../../assets/javascript.png';
import react from '../../assets/reactjs.png';
import ads from '../../assets/ads.png';
import content from '../../assets/content.png';
import seo from '../../assets/seo.png'

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: html,
        },
        {
            count: 1.5,
            label: 'Year of Experience',
            img: css,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: javascripti,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: react,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: ads,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: content,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: seo,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 60s linear infinite', // Slowed down scrolling
    };

    const imgStyle = {
        display: 'inline-block',
        mr: '90px',
        width: { md: '100px', sm: '100%' },
        height: { md: '100px', sm: '100%' }, // Adjust height proportionally
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        maxHeight: { md: '100px', sm: '100%' }, // Increased height of images
        borderRadius: '8px',
        mt: 3
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        <Grid
            container
            sx={{
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 0, xs: 0 },

                // Increased grid item width

                alignItems: 'stretch', // Make children fill container height fully

            }}
        >
            <Box sx={{ width: '90%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '2.5rem', xs: '2rem' },
                        textAlign: 'center',
                        mb: 3, // Added margin-bottom
                    }}
                >
                    My Skills
                </Typography>
            </Box>
            {/* <Grid container sx={{ width: { md: '20vw', sm: '90vw', xs: '90vw' }, }}> */}
            <Grid item xs={6} md={4} sm={6}>
                <Box
                    sx={{
                        backgroundColor: '#fff',
                        py: { xs: 3, md: 0 },
                        px: 2,
                        mt: 2,
                        ml: { md: 10, xs: 3, sm: 5 },
                        mr: 10,
                        width: { md: '90vw', sm: '90vw', xs: '90vw' },
                        borderRadius: '50px',
                        overflow: 'hidden',
                        height: '150px', // Increased height to fit larger images
                    }}
                >

                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {/* First set of images */}
                        {stats.map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}

                    </Box>
                </Box>

            </Grid>
        </Grid>
    );
};

export default Experince;
