import { Grid, Typography, Box, TextField, Button } from '@mui/material';
import React from 'react';

const Contact = () => {
    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 10, xs: 3, sm: 6 },
                py: { md: 6, xs: 4 },
                mt: { md: -8, xs: 3 },
                minHeight: { xs: '100vh', md: '90vh' },
                overflow: 'hidden',
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    Contact Me
                </Typography>

                {/* Contact Form */}
                <Box
                    component="form"
                    sx={{
                        maxWidth: 600,
                        margin: "0 auto",
                        borderRadius: 3,
                        boxShadow: 6,
                        p: { xs: 3, md: 5 },
                        display: "flex",
                        flexDirection: "column",
                        gap: 3,
                        mt: 5,
                        color: 'white',
                        backdropFilter: "blur(10px)", // Glassmorphism
                        backgroundColor: "rgba(255,255,255,0.05)", // Transparent feel
                        border: "1px solid rgba(255,255,255,0.2)",
                    }}
                >
                    <TextField
                        label="Full Name"
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "10px" },
                        }}
                    />
                    <TextField
                        label="Email Address"
                        type="email"
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "10px" },
                        }}
                    />
                    <TextField
                        label="Message"
                        multiline
                        rows={4}
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "10px" },
                        }}
                    />
                    <Button
                        type="submit"
                        variant="contained"
                        sx={{
                            backgroundColor: "white",
                            color: 'rgb(13, 1, 33)',
                            "&:hover": { backgroundColor: "#4a2a87", color: "white" },
                            fontSize: "1rem",
                            textTransform: "none",
                            fontWeight: "bold",
                            borderRadius: "30px",
                            py: 1.5,
                        }}
                    >
                        Submit
                    </Button>
                </Box>
            </Box>
        </Grid>
    );
};

export default Contact;
