import React from 'react';
import { Grid, Container, Box, Typography } from '@mui/material';
import portfolio1 from '../../assets/reactjs.png'; // Replace with actual image paths
import portfolio2 from '../../assets/html.png';
import portfolio3 from '../../assets/javascript.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: portfolio1,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: portfolio2,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: portfolio3,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 10s linear infinite',
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '20px',
        width: '200px',
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
    };

    const textStyle = {
        color: 'white',
        fontSize: '0.8rem',
        textAlign: 'center',
        marginTop: '10px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        <Container
            maxWidth={false}
            sx={{
                backgroundColor: '#fff',
                py: { xs: 2, md: 2 },
                px: 2,
                width: { md: '95vw', sm: '85vw', xs: '90vw' },
                borderRadius: 2,
                overflow: 'hidden', // To hide overflowed images
            }}
        >

            <Grid
                container
                spacing={2} // Increased spacing between items
                justifyContent="center"
                alignItems="center"
            >

                {/* Typography Section before Scrolling Images */}
                <Grid item xs={12} sx={{ textAlign: 'center', mb: 3 }}>
                    <Typography
                        variant="h4"
                        sx={{
                            color: 'black',
                            fontWeight: 'bold',
                            fontSize: { xs: '1.5rem', md: '2rem' },
                        }}
                    >
                        My Expertise
                    </Typography>
                    <Typography
                        variant="body1"
                        sx={{
                            color: 'black',
                            fontSize: { xs: '1rem', md: '1.2rem' },
                            marginTop: 2,
                            textAlign: 'center',
                        }}
                    >
                        With over {stats[0].count} years of experience, I've successfully completed {stats[1].count}+ projects and worked alongside a team of {stats[2].count} expert employees. Here's a glimpse of my journey.
                    </Typography>
                </Grid>

                {/* Scrolling Image Section */}
                <Grid item xs={12} sx={{ overflow: 'hidden' }}>
                    {/* Inline styles for auto-scrolling image container */}
                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {stats.map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}
                    </Box>
                </Grid>

            </Grid>
        </Container>
    );
};

export default Experince;
