import React, { useEffect, useState } from 'react'
import PropTypes from 'prop-types';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import { Grid , Fab, useScrollTrigger} from '@mui/material';
import Header from './Header';
import Banner from './Banner';
import About from './About';
import Colleges from './Colleges'
import Courosel from './Courosel';
import Status from './Status'

import Fade from '@mui/material/Fade';

function ScrollTop(props) {
  const { children, window } = props;

  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');
    if (anchor) {
      anchor.scrollIntoView({
        behavior: 'smooth',
        block: 'start',
      });
    }
  };

  return (
    <Fade in={trigger}>
      <div 
        onClick={handleClick} 
        role="presentation" 
        style={{ 
          position: 'fixed', 
          bottom: 16, 
          right: 16, 
          zIndex: 1000 // Ensure it stays on top
        }}
      >
        {children}
      </div>
    </Fade>
  );
}

ScrollTop.propTypes = {
  children: PropTypes.element.isRequired,
  window: PropTypes.func,
};

const Home = (props) => {
  const [isAutoPopupOpen, setIsAutoPopupOpen] = useState(false);

  const handleOpenAutoPopup = () => {
    const formSubmitted = sessionStorage.getItem("formSubmitted") === "yes";
    if (!formSubmitted) {
      setIsAutoPopupOpen(true);
    }
  };
  const handleCloseAutoPopup = () => {
    setIsAutoPopupOpen(false);
  };
  useEffect(() => {
    const timeout = setTimeout(() => {
      handleOpenAutoPopup();
    }, 5000);

    return () => clearTimeout(timeout);
  }, []);
  return (
    <Grid sx={{ overflowX: 'hidden', }}>
      <div id="back-to-top-anchor" />
      <Header />
      <Banner/>
      <About/>
     <Status/>
     {/* <Colleges/> */}
    </Grid>
  )
}

export default Home
