import { Grid, Typography, Box } from '@mui/material';
import React from 'react';
import aboutImg from '../../assets/portfolio-1.png'; // Replace with your preferred image
import Experince from './Experience';

const Works = () => {
    return (
        <Grid
            container
            sx={{
                backgroundColor: 'rgb(30, 30, 30)',
                px: { md: 10, xs: 3, sm: 6 },
                py: { md: 6, xs: 4, sm: 2 },
                minHeight: { xs: '100vh', md: '90vh' },
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            {/* LEFT SIDE: Text Content */}
            <Grid item xs={12} md={6}>
                <Box sx={{ width: '100%' }}>
                    <Typography
                        variant="h2"
                        sx={{
                            color: 'white',
                            fontWeight: 600,
                            fontFamily: 'inherit',
                            fontSize: { md: '3rem', xs: '2rem' },
                            textAlign: { xs: 'center', md: 'left' },
                        }}
                    >
                        About Me
                    </Typography>

                    <Typography
                        variant="body2"
                        sx={{
                            color: 'lightgray',
                            fontFamily: 'inherit',
                            fontSize: { md: '1.2rem', xs: '0.95rem', sm: '1.1rem' },
                            lineHeight: 1.6,
                            textAlign: { xs: 'center', md: 'left' },
                            mt: 2,
                            mb: 4,
                        }}
                    >
                        I take pride in paying attention to the smallest details and ensuring every element of a website is both visually polished and functionally precise. From responsive layouts and intuitive navigation to performance optimization and accessibility, I strive to deliver pixel-perfect, user-friendly web experiences. I am passionate about combining clean code with creative design to build websites that not only look professional but also drive results.
                    </Typography>

                    <Experince />
                </Box>
            </Grid>

            {/* RIGHT SIDE: Image */}
            <Grid item xs={12} md={6}>
                <Box
                    component="img"
                    src={aboutImg}
                    alt="About Me"
                    sx={{
                        width: '100%',
                        height: '100%',
                        maxHeight: { xs: 300, md: 500 },
                        objectFit: 'cover',
                        borderRadius: 4,
                        mt: { xs: 4, md: 0 },
                    }}
                />
            </Grid>
        </Grid>
    );
};

export default Works;
