import React from 'react';
import { Grid, Container, Box, Typography } from '@mui/material';
import html from '../../assets/html.png'; // Replace with actual image paths
import css from '../../assets/css.png';
import javascripti from '../../assets/javascript.png';
import react from '../../assets/reactjs.png';
import ads from '../../assets/ads.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: html,
        },
        {
            count: 1.5,
            label: 'Year of Experience',
            img: css,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: javascripti,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: react,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: ads,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 20s linear infinite', // Slowed down scrolling
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '40px', // Increased space between images
        width: '200px',
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        maxHeight: '150px', // Reduced height of images
        borderRadius: '8px',
    };

    const textStyle = {
        color: 'black',
        fontSize: '1rem', // Adjust font size
        textAlign: 'center',
        marginTop: '10px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        // <Container>
        <Grid
            container
            spacing={2} // Increased spacing between items
            justifyContent="center"
            alignItems="center"
        >
            <Box sx={{ width: '90%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'black',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '2.5rem', xs: '2rem' },
                        textAlign: 'center',
                        mb: 3, // Added margin-bottom
                    }}
                >
                    My Skills
                </Typography>
            </Box>

            <Grid
                item
                xs={12}
                maxWidth={false}
                sx={{
                    backgroundColor: '#fff',
                    py: { xs: 3, md: 0 },
                    px: 2,
                    mt: 2,
                    width: { md: '90vw', sm: '85vw', xs: '90vw' },
                    borderRadius: 2,
                    overflow: 'hidden', // To hide overflowed images
                }}
            >
                {/* Inline styles for auto-scrolling image container */}
                <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                    {stats.map((stat, index) => (
                        <Box key={index} sx={imgStyle}>
                            <Box
                                component="img"
                                src={stat.img}
                                alt={`portfolio ${index}`}
                                sx={{ height: '70px' }}
                            />

                        </Box>
                    ))}
                </Box>
            </Grid>
        </Grid>
        // </Container>
    );
};

export default Experince;
