import { Grid, Typography, Box, Card, CardMedia } from '@mui/material';
import React from 'react';
import portfolio1 from '../../assets/portfolio-1.png';
import portfolio2 from '../../assets/portfolio-2.png';
import portfolio3 from '../../assets/portfolio-3.png';
import portfolio4 from '../../assets/portfolio-4.png'
import portfolio5 from '../../assets/portfolio-5.png'
import portfolio6 from '../../assets/portfolio-6.png'
import Experince from './Experience';

const skillsData = [
    {
        id: 1,
        img: portfolio1,
    },
    {
        id: 2,
        img: portfolio2,

    },
    {
        id: 3,
        img: portfolio3,

    },
    {
        id: 4,
        img: portfolio4,

    },
    {
        id: 5,
        img: portfolio5,

    },
    {
        id: 6,
        img: portfolio6,

    },
];

const Works = () => {
    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(30, 30, 30)',
                px: { md: 10, xs: 3, sm: 6 },
                py: { md: 6, xs: 4, sm: 2 },
                mt: { md: -8, xs: 3 },
                minHeight: { xs: '100vh', md: '90vh' },
                overflow: 'hidden',
                justifyContent: 'center',
            }}
        >
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    My Portofolio
                </Typography>

                <Typography
                    variant="body2"
                    sx={{
                        color: 'lightgray',
                        fontFamily: 'inherit',
                        fontSize: { md: '1.2rem', xs: '0.9rem', sm: '1.2rem', },
                        lineHeight: 1.5,
                        textAlign: 'center',
                        mt: 2,
                        mb: 5,
                    }}
                >
                    I take pride in paying attention to the smallest details and ensuring every element of a website is both visually polished and functionally precise. From responsive layouts and intuitive navigation to performance optimization and accessibility, I strive to deliver pixel-perfect, user-friendly web experiences. I am passionate about combining clean code with creative design to build websites that not only look professional but also drive results.
                </Typography>
                <Grid >
                    <Experince />
                </Grid>
                {/* Skills List Style */}
                <Grid container spacing={3} justifyContent="center">
                    {skillsData.map((skill, index) => (
                        <Grid item xs={12} md={4} key={skill.id} mt={5}>
                            <Card
                                sx={{
                                    background: 'lightgray',
                                    color: '#1e1e1e',
                                    height: '300px',
                                    borderRadius: 2,
                                    overflow: 'hidden',
                                    animation: 'zoomInOut 5s ease-in-out infinite',
                                    animationDelay: `${index * 0.5}s`, // staggered delay
                                    '@keyframes zoomInOut': {
                                        '0%': {
                                            transform: 'scale(1)',
                                        },
                                        '50%': {
                                            transform: 'scale(1.05)',
                                        },
                                        '100%': {
                                            transform: 'scale(1)',
                                        },
                                    },
                                }}
                            >
                                <CardMedia
                                    component="img"
                                    src={skill.img}
                                    alt={`Portfolio ${skill.id}`}
                                    sx={{
                                        width: '100%',
                                        height: '100%',
                                        objectFit: 'cover',
                                        borderRadius: '50%'
                                    }}
                                />
                            </Card>
                        </Grid>
                    ))}
                </Grid>

            </Box>
        </Grid>
    );
};

export default Works;
