import React, { useEffect, useRef, useState } from 'react';
import {
    Box,
    Card,
    CardContent,
    Typography,
    Stack,
    Container,
    Grid,
} from '@mui/material';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';

/* ================================
   PDFViewer Component (auto-scroll on hover)
================================ */
function PDFViewer({ url, height = 300 }) {
    const scrollRef = useRef(null);
    const scrollIntervalRef = useRef(null);
    const [isHovering, setIsHovering] = useState(false);

    useEffect(() => {
        const container = scrollRef.current;
        if (!container) return;

        if (isHovering) {
            scrollIntervalRef.current = setInterval(() => {
                if (container.scrollTop + container.clientHeight >= container.scrollHeight) {
                    container.scrollTop = 0;
                } else {
                    container.scrollTop += 1;
                }
            }, 30);
        } else {
            clearInterval(scrollIntervalRef.current);
        }

        return () => clearInterval(scrollIntervalRef.current);
    }, [isHovering]);

    if (!url) {
        return (
            <Box
                sx={{
                    height,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    bgcolor: '#f5f5f5',
                }}
            >
                <Typography variant="body2" color="text.secondary">
                    No PDF provided
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            ref={scrollRef}
            sx={{
                height,
                overflowY: 'scroll',
                borderRadius: 2,
                scrollbarWidth: 'none',
                '&::-webkit-scrollbar': { display: 'none' },
                cursor: 'pointer',
                boxShadow: 3,
            }}
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
        >
            <iframe
                src={url}
                width="100%"
                height="1200px"
                frameBorder="0"
                title="pdf-viewer"
                style={{
                    pointerEvents: 'none',
                    borderRadius: 8,
                }}
            />
        </Box>
    );
}

/* ================================
   Main Component: PDF Cards
================================ */
export default function PPTCards() {
    const pdfUrls = [
        '/getmoredigital.pdf',
        '/pucolleges.pdf',
        '/getmoredigital.pdf',
    ];

    return (
        <Grid cotainer sx={{ py: 4, px: { md: 10, xs: 3, sm: 6 }, }}>
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 600,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    Projects
                </Typography>

                <Typography
                    variant="body2"
                    sx={{
                        color: 'lightgray',
                        fontFamily: 'inherit',
                        fontSize: { md: '1.2rem', sm: '1.3rem', xs: '0.9rem' },
                        lineHeight: 2,
                        textAlign: 'center',
                        mt: 2,
                        mb: 5,

                    }}
                >
                    <FormatQuoteIcon sx={{ color: 'orange', transform: 'rotate(180deg)', mt: '-8px', fontSize: '30px' }} /> Here is a showcase of my projects that demonstrate my expertise in building modern web applications and dynamic e-commerce platforms. Each project reflects my ability to blend creative design with robust development, delivering user-friendly, scalable, and results-driven digital solutions <FormatQuoteIcon sx={{ color: 'orange', fontSize: '30px' }} />
                </Typography>

            </Box>
            <Stack direction={{ xs: 'column', md: 'row' }} spacing={3}>
                {pdfUrls.map((url, index) => (
                    <Card key={index} sx={{ flex: 1 }}>
                        <PDFViewer url={url} height={300} />
                        <CardContent>
                            <Typography variant="h6">{`Presentation ${index + 1}`}</Typography>
                            <Typography variant="body2" color="text.secondary">
                                Hover to auto-scroll PDF content.
                            </Typography>
                        </CardContent>
                    </Card>
                ))}
            </Stack>
        </Grid>
    );
}
