import React, { useState } from 'react';
import { Grid, Card, CardContent, Typography, Box, CardMedia } from '@mui/material';
import princy from '../assets/cards image/princy.png';
import image2 from '../assets/cards image/princy.png';
import image3 from '../assets/cards image/princy.png';

const imageList = [princy, image2, image3]; // Array of image sources
const names = ["Ramanuj", "Shankari", "Kumar"]; // Array of names

const Cards = () => {
  const [activeCard, setActiveCard] = useState(null);

  const toggleCard = (index) => {
    setActiveCard(activeCard === index ? null : index);
  };

  return (
    <Grid
      container
      spacing={2}
      sx={{ background: 'linear-gradient(to top,#ffffff, #D9EEE1)', height: '80vh', padding: 2 }}
    >
      {imageList.map((image, index) => (
        <Grid item xs={12} sm={4} key={index}>
          {/* Main Card */}
          <Card
            sx={{
              background: 'yellow',
              height: '60%',
              cursor: 'pointer',
            }}
            onClick={() => toggleCard(index)}
          >
            <CardMedia>
              <img
                src={image}
                alt={`Image ${index + 1}`}
                style={{ height: '80%', width: '100%', objectFit: 'cover' }}
              />
            </CardMedia>
            <CardContent>
              <Typography variant="h6" align="center">
                Name: {names[index]}
              </Typography>
              <Typography align="center" sx={{ marginTop: 1 }}>
                {activeCard === index ? 'Click to Close' : 'Click to Expand'}
              </Typography>
            </CardContent>
          </Card>

          {/* Additional Content */}
          {activeCard === index && (
            <Card
              sx={{
                backgroundColor: 'lightgreen',
                marginTop: 2,
                padding: 2,
              }}
            >
              <CardContent>
                <Typography variant="body1">
                  With a focus on digital marketing, we empower our clients to reach their goals and enhance their
                  online visibility. Our team of professionals uses state-of-the-art technologies to boost digital
                  presence and drive growth.
                </Typography>
              </CardContent>
            </Card>
          )}
        </Grid>
      ))}
    </Grid>
  );
};

export default Cards;
