import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import Carousel from 'react-material-ui-carousel';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import banner2 from '../assets/cloud.png';

const carouselItems = [
  {
    image: banner1,
    title: "Welcome to Siddaganga College",
    description: "Empowering Future Leaders in Management and Science",
  },
  {
    image: banner2,
    title: "Join Our Programs",
    description: "Explore a wide range of courses designed for holistic development",
  },
];

const BackgroundCarousel = () => {
  return (
    <Carousel
      indicators={true}
      animation="slide"
      navButtonsAlwaysVisible={true}
    >
      {carouselItems.map((item, index) => (
        <Box
          key={index}
          sx={{
            height: '500px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#fff',
            textAlign: 'center',
            backgroundImage: `url(${item.image})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            padding: '20px',
          }}
        >
          <Typography variant="h3" sx={{ fontWeight: 600, mb: 2 }}>
            {item.title}
          </Typography>
          <Typography variant="h6" sx={{ mb: 3 }}>
            {item.description}
          </Typography>
          <Button variant="contained" color="primary">
            Learn More
          </Button>
        </Box>
      ))}
    </Carousel>
  );
};

export default BackgroundCarousel;
