import React, { useState } from 'react';
import { Grid, Typography, Button, Box, TextField, Alert, Snackbar } from '@mui/material';

const Connect = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
  });

  const [errors, setErrors] = useState({
    name: false,
    phone: false,
  });

  const [success, setSuccess] = useState(false);
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^[0-9]{10}$/;
    return phoneRegex.test(phone);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isNameValid = formData.name.trim() !== '';
    const isPhoneValid = validatePhone(formData.phone);

    if (!isNameValid || !isPhoneValid) {
      setErrors({
        name: !isNameValid,
        phone: !isPhoneValid,
      });
      setOpenSnackbar(false); // Hide success if validation fails
      return;
    }

    // Show success snackbar
    setErrors({ name: false, phone: false });
    setSuccess(true);
    setOpenSnackbar(true);

    // Clear form data
    setFormData({ name: '', phone: '' });
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenSnackbar(false);
  };

  return (
    <Grid
      container
      spacing={2}
      sx={{
        backgroundColor: '#efefef',
        padding: '30px 80px',
        alignItems: 'center',
      }}
    >
      {/* Title Section */}
      <Grid item xs={12}>
        <Typography
          variant="h3"
          sx={{
            fontWeight: 600,
            fontSize: { xs: '24px', md: '30px' },
            mt: 1,
            color: '#907394',
            textAlign: 'center',
            mb: 5,
          }}
        >
          Connect Now
        </Typography>
      </Grid>

      <Grid container sx={{ padding: 10, backgroundColor: 'white' }}>
        {/* Form Section */}
        <Grid
          item
          xs={12}
          md={8}
          sx={{
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            justifyContent: 'center',
            alignItems: 'center',
            gap: 2,
            mx: 'auto',
          }}
        >
          {/* Name Field */}
          <Box sx={{ width: { xs: '100%', md: '45%' } }}>
            <TextField
              fullWidth
              required
              id="name-field"
              label="Name"
              variant="standard"
              name="name"
              value={formData.name}
              onChange={handleChange}
              error={errors.name}
              helperText={errors.name ? 'Name is required' : ''}
            />
          </Box>

          {/* Phone Number Field */}
          <Box sx={{ width: { xs: '100%', md: '45%' } }}>
            <TextField
              fullWidth
              required
              id="phone-field"
              label="Phone Number"
              variant="standard"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              error={errors.phone}
              helperText={errors.phone ? 'Enter a valid 10-digit phone number' : ''}
            />
          </Box>
        </Grid>

        {/* Submit Button */}
        <Grid item xs={12} md={4} sx={{ textAlign: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            sx={{
              mt: { xs: 2, md: 0 },
              px: 4,
              borderRadius: '30px',
              backgroundColor: '#335064',
              '&:hover': {
                backgroundColor: '#705a6f',
              },
            }}
            onClick={handleSubmit}
          >
            Submit
          </Button>
        </Grid>
      </Grid>

      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={handleClose}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          onClose={handleClose}
          severity="success"
          sx={{
            textAlign: 'center',
            backgroundColor: 'white',
            color: 'purple',
          }}
        >
          Form submitted successfully!
        </Alert>
      </Snackbar>
    </Grid>
  );
};

export default Connect;
