import { Grid, Typography, Box, Card, CardContent, CardMedia } from '@mui/material';
import React from 'react';
import ui from '../../assets/ui-design.png'

const Skills = () => {
  return (
    <Grid
      container
      sx={{
        position: 'relative',
        backgroundColor: 'rgb(30, 30, 30)',
        px: { md: 10, xs: 3 },
        mt: { md: -2, xs: 3 },
        height: { xs: '80vh', md: '90vh' },
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'center',
        // alignItems: 'center', // centers vertically
        textAlign: 'center',
      }}
    >
      <Box>
        <Typography
          variant="h2"
          sx={{
            color: 'white',
            fontWeight: 600,
            fontFamily: 'inherit',
            fontSize: { md: '3rem', xs: '2rem' },
            mb: 0, // remove bottom margin
          }}
        >
          What I Do
        </Typography>

        <Typography
          variant='body2'
          sx={{
            color: 'lightgray',
            fontFamily: 'inherit',
            fontSize: { md: '1.5rem', xs: '0.9rem' },
            lineHeight: 1.5,
            mt: 2
          }}
        >

          I am a skilled and passionate web designer with experience in
          creating visually appealing and user-friendly websites. I have a strong
          understanding of design and a keen eye for detail. I am proficient in HTML,
          CSS, JavaScript, and React.js, as well as design software such as Adobe Photoshop.
        </Typography>
        {/* <Grid> */}
        <Grid item xs={12} md={6}>
          <Card sx={{ background: 'lightwhite' }}>
            <CardMedia>
              <img src={ui} />
              <Typography variant='h3'>UI/UX Design </Typography>
            </CardMedia>
            <CardContent sx={{ height: '20vh' }}>


            </CardContent>
          </Card>

        </Grid>

        {/* </Grid> */}
      </Box>

    </Grid>
  );
};

export default Skills;
