import { Grid, Typography , Button, Box} from '@mui/material'
import React from 'react'
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';
import { motion } from 'framer-motion';

const About = () => {
    return (
        <Grid container
            sx={{
                backgroundColor: 'linear-gradient(to left, #e0f7fa, #ffffff )',
                // backgroundColor:'green',
                display: 'flex',
                width:{md:'100%', sm:'100%'},
                height:'100%',
                padding:'50px  30px',
                mt:{xs:-1, sm:0, md:3}
            }}>
            <Grid item xs={12} md={7} sx={{justifyContent:'space-between'}}padding={2}  >
                <Typography variant='h3' sx={{fontWeight:600, fontsise:'30px' ,mt:1, color:'#907394'}}>
                About Us
                </Typography>
             <Typography variant='body1' sx={{fontSize:'18px',  mt:4, textAlign:'justify'}}>
             Renowned for its dedication to educational quality Siddaganga Institute is a preeminent establishment committed to equipping students with information and abilities in a variety of subjects. It provides a dynamic learning environment with a history of high-quality instruction cutting-edge facilities and an emphasis on holistic development. By emphasizing innovation research and hands-on training the institute equips students to succeed in their chosen fields and become professionals who are ready for the workforce so promoting the growth and development of the country.
             </Typography>
             <Box sx={{ display: 'flex', justifyContent: 'left', mt: 2, width:50, }}>
    <Button variant='contained' sx={{backgroundColor:'#335064', borderRadius:'20px', }}>Know More</Button>
  </Box>
            </Grid>
            <Grid 
            item 
            xs={12} 
            md={5} 
            sx={{ justifyContent: 'right', height: '100%', display: 'flex', alignItems: 'center',
                justifyContent: 'center', }}
        >
            <motion.img
                src={banner1}
                alt="banner1"
                style={{ 
                    width: '90%', 
                    height: '60%',
                    borderRadius: '2%', 
                    boxShadow: '15px 15px 8px rgba(0, 0, 0, 0.8) ' 
                }}
                whileHover={{ scale: 1.1 }}  // Zoom in on hover
                whileTap={{ scale: 1.05 }}  // Optional: Slightly shrink on click
                transition={{ duration: 0.3, ease: 'easeInOut' }}
            />
        </Grid>

        </Grid>
    )
}

export default About
