import { Grid, Typography, Box, TextField, Button } from '@mui/material';
import React from 'react';

const Contact = () => {
    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 10, xs: 3, sm: 6 },
                // py: { md: 8, xs: 0 },
                // mt: { md: -8, xs: 0 },
                minHeight: { xs: '70vh', md: '90vh' },
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 700,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    Contact Me
                </Typography>

                <Typography
                    variant="body1"
                    sx={{
                        color: 'lightgray',
                        textAlign: 'center',
                        mt: 2,
                        fontSize: { md: '1.2rem', xs: '1rem' },
                        maxWidth: 700,
                        mx: "auto",
                    }}
                >
                    I’d love to hear from you! Fill out the form below and I’ll get back to you as soon as possible.
                </Typography>

                {/* Contact Form */}
                <Box
                    component="form"
                    sx={{
                        maxWidth: 450,
                        margin: "0 auto",
                        borderRadius: 3,
                        p: { xs: 3, md: 5 },
                        display: "flex",
                        flexDirection: "column",
                        gap: 3,
                        mt: 5,
                        backdropFilter: "blur(12px)", // smooth glass effect
                        backgroundColor: "rgba(255,255,255,0.07)",
                        // border: "1px solid rgba(255,255,255,0.2)",
                        boxShadow: "0px 8px 30px rgba(0,0,0,0.5)",
                        border: '5px solid yellow',
                    }}
                >
                    <TextField
                        label="Full Name"
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "12px" },
                        }}
                        sx={{
                            "& .MuiOutlinedInput-root": {
                                "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                "&:hover fieldset": { borderColor: "white" },
                                "&.Mui-focused fieldset": { borderColor: "#a29bfe", boxShadow: "0 0 8px #a29bfe" },
                            },
                        }}
                    />
                    <TextField
                        label="Email Address"
                        type="email"
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "12px" },
                        }}
                        sx={{
                            "& .MuiOutlinedInput-root": {
                                "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                "&:hover fieldset": { borderColor: "white" },
                                "&.Mui-focused fieldset": { borderColor: "#a29bfe", boxShadow: "0 0 8px #a29bfe" },
                            },
                        }}
                    />
                    <TextField
                        label="Message"
                        multiline
                        rows={4}
                        variant="outlined"
                        fullWidth
                        required
                        InputLabelProps={{ style: { color: 'lightgray' } }}
                        InputProps={{
                            style: { color: 'white', borderRadius: "12px" },
                        }}
                        sx={{
                            "& .MuiOutlinedInput-root": {
                                "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                "&:hover fieldset": { borderColor: "white" },
                                "&.Mui-focused fieldset": { borderColor: "#a29bfe", boxShadow: "0 0 8px #a29bfe" },
                            },
                        }}
                    />
                    <Button
                        type="submit"
                        variant="contained"
                        sx={{
                            background: "linear-gradient(135deg, #ffffff, #dfe6e9)",
                            color: 'rgb(13, 1, 33)',
                            "&:hover": { background: "linear-gradient(135deg, #4a2a87, #6c5ce7)", color: "white" },
                            fontSize: "1rem",
                            textTransform: "none",
                            fontWeight: "bold",
                            borderRadius: "30px",
                            py: 1.5,
                            transition: "0.3s ease",
                        }}
                    >
                        Submit
                    </Button>
                </Box>
            </Box>
        </Grid>
    );
};

export default Contact;
