import React from 'react';
import { Box, useMediaQuery, useTheme } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Slider from 'react-slick';

import ArrowCircleLeftRoundedIcon from '@mui/icons-material/ArrowCircleLeftRounded';
import ArrowCircleRightRoundedIcon from '@mui/icons-material/ArrowCircleRightRounded';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';

// Slide data
const slidesData = [
  {
    title: "Digital Marketing Courses - Strategy and Resources",
    description: "Unlock the full potential of digital marketing power with comprehensive training that equips you to thrive online.",
    image: banner1,
    buttonText: "Explore More", path: '/digital-marketing',
  },
  // Other slides can be added here
];

const Arrow = ({ className, style, onClick, direction }) => {
  const isMobile = useMediaQuery('(max-width:600px)');
  return (
    <Box
      className={className}
      sx={{
        ...style,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '20%',
        cursor: 'pointer',
        zIndex: 1,
        width: '40px',
        height: '40px',
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        [direction === 'left' ? 'left' : 'right']: isMobile ? '20px' : '70px',
      }}
      onClick={onClick}
    >
      {direction === 'left' ? <ArrowCircleLeftRoundedIcon /> : <ArrowCircleRightRoundedIcon />}
    </Box>
  );
};

const Banner = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isTablet = useMediaQuery(theme.breakpoints.between('sm', 'md'));

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1, // Ensure only one slide is shown at a time
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <Arrow direction="right" />,
    prevArrow: <Arrow direction="left" />,
    customPaging: (i) => (
      <Box
        sx={{
          // Custom styling for pagination dots can go here
        }}
      />
    ),
  };

  return (
    <Box
      sx={{
        width: '100%',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '550px',
        position: 'relative',
      }}
    >
      {/* Slider component */}
      <Slider {...sliderSettings}>
        {slidesData.map((slide, index) => (
          <Box key={index}>
            <Box
              sx={{
                display: 'flex',
                // mt: 5,
                flexDirection: isMobile ? 'column' : 'row',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              {/* Image Box */}
              <Box sx={{ width:  '100%', height:'70%' }}>
                <img
                  src={slide.image}
                  alt={slide.title}
                  style={{
                    width: '100%',
                    height: '100%',

                  }}
                />
              </Box>
            </Box>
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default Banner;
