import React from 'react';
import { Grid, Box } from '@mui/material';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 

const CustomArrow = ({ direction, onClick }) => {
    return (
      <Box
        onClick={onClick}
        onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.2)')}
        onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
        style={{
          display: 'block',
          zIndex: 2,
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background
          borderRadius: '50%', // Circular button
          padding: '10px',
          cursor: 'pointer', // Pointer on hover
          transition: 'transform 0.3s ease', // Smooth animation
          transform: 'scale(1)', // Initial scale
          position: 'absolute',
          top: '50%',
          transform: 'translateY(-50%)', // Vertically center
          right: direction === 'next' ? '15px' : 'unset',
          left: direction === 'prev' ? '15px' : 'unset',
        }}
      >
      </Box>
    );
  };

const Banner = () => {
  // Slider settings
  const settings = {
    dots: false,
    infinite: true,
    speed: 500, // Smooth transition speed
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrow direction="next" />,
    prevArrow: <CustomArrow direction="prev" />,
  };

  // Array of images
  const images = [banner1, banner2, banner3];


  return (
    <Grid container  sx={{maxHeight:'60vh'}}>

      <Slider {...settings}>
          {images.map((image, index) => (
            <Box key={index} sx={{ px: { xs: 2, sm: 4 } }}> {/* Padding for slider */}
              <img
                src={image}
                alt='image'
                style={{
                  width: '100%',
                  height: 'auto', // Ensures full image is displayed
                  maxHeight: '60vh', // Limit height for consistent appearance
                  objectFit: 'contain', // Adjust to ensure full image is visible
                  borderRadius: '8px', // Optional styling for rounded edges
                }}
              />
            </Box>
          ))}
        </Slider>
      </Grid>

  )
}

export default Banner
