import { Grid, Typography, Box, TextField, Button, Alert } from '@mui/material';
import React, { useState } from 'react';

const Contact = () => {
    const [formData, setFormData] = useState({ name: '', email: '', message: '' });
    const [errors, setErrors] = useState({});
    const [submitted, setSubmitted] = useState(false);

    // Handle input change
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Validate form
    const validate = () => {
        const tempErrors = {};
        if (!formData.name.trim()) tempErrors.name = 'Full Name is required';
        if (!formData.email.trim()) tempErrors.email = 'Email is required';
        else if (!/\S+@\S+\.\S+/.test(formData.email))
            tempErrors.email = 'Email is invalid';
        if (!formData.message.trim()) tempErrors.message = 'Message is required';
        setErrors(tempErrors);
        return Object.keys(tempErrors).length === 0;
    };

    // Handle submit
    const handleSubmit = (e) => {
        e.preventDefault();
        if (validate()) {
            console.log('Form submitted:', formData);
            setSubmitted(true);
            setFormData({ name: '', email: '', message: '' });
            setErrors({});
            // Optional: hide alert after 3s
            setTimeout(() => setSubmitted(false), 3000);
        }
    };

    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(13, 1, 33)',
                px: { md: 10, xs: 3, sm: 6 },
                minHeight: { xs: '70vh', md: '90vh' },
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <Box sx={{ width: '100%' }}>
                {/* Heading */}
                <Typography
                    variant="h2"
                    sx={{
                        color: 'white',
                        fontWeight: 700,
                        fontFamily: 'inherit',
                        fontSize: { md: '3rem', xs: '2rem' },
                        textAlign: 'center',
                    }}
                >
                    Contact Me
                </Typography>

                <Typography
                    variant="body1"
                    sx={{
                        color: 'lightgray',
                        textAlign: 'center',
                        mt: 2,
                        fontSize: { md: '1.2rem', xs: '1rem' },
                        maxWidth: 700,
                        mx: "auto",
                    }}
                >
                    I‚Äôd love to hear from you! Fill out the form below and I‚Äôll get back to you as soon as possible.
                </Typography>

                {/* Thank you alert */}
                {submitted && (
                    <Alert severity="success" sx={{ mt: 3, maxWidth: 450, mx: 'auto' }}>
                        Thank you! Your message has been submitted. I will Connect you soon
                    </Alert>
                )}

                <Grid item xs={12} md={6}>
                    <Typography
                        variant="h3"
                        sx={{
                            fontWeight: 700,
                            fontFamily: 'inherit',
                            fontSize: { md: '2.5rem', xs: '2rem' },
                            textAlign: { xs: 'center', md: 'left' },
                            mb: 2,
                        }}
                    >
                        Contact Me
                    </Typography>
                    <Typography
                        variant="body1"
                        sx={{
                            color: 'lightgray',
                            textAlign: { xs: 'center', md: 'left' },
                            mb: 4,
                            fontSize: { md: '1.2rem', xs: '1rem' },
                        }}
                    >
                        I‚Äôd love to hear from you! Fill out the form below and I‚Äôll get back to you as soon as possible.
                    </Typography>

                    {submitted && (
                        <Alert severity="success" sx={{ mb: 3 }}>
                            ‚úÖ Thank you! Your message has been submitted. I will connect with you soon.
                        </Alert>
                    )}

                    <Box
                        component="form"
                        sx={{
                            borderRadius: 12,
                            p: { xs: 3, md: 4 },
                            display: "flex",
                            flexDirection: "column",
                            gap: 3,
                            backdropFilter: "blur(12px)",
                            backgroundColor: "rgba(255,255,255,0.07)",
                            boxShadow: "0px 8px 30px rgba(0,0,0,0.5)",
                            border: "2px solid yellow",
                        }}
                        onSubmit={handleSubmit}
                    >
                        <TextField
                            label="Full Name"
                            name="name"
                            variant="outlined"
                            fullWidth
                            value={formData.name}
                            onChange={handleChange}
                            error={!!errors.name}
                            helperText={errors.name}
                            InputLabelProps={{ style: { color: 'lightgray' } }}
                            InputProps={{ style: { color: 'white', borderRadius: "12px" } }}
                            sx={{
                                "& .MuiOutlinedInput-root": {
                                    "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                    "&:hover fieldset": { borderColor: "white" },
                                    "&.Mui-focused fieldset": {
                                        borderColor: "#a29bfe",
                                        boxShadow: "0 0 8px #a29bfe"
                                    },
                                },
                            }}
                        />

                        <TextField
                            label="Email Address"
                            name="email"
                            type="email"
                            variant="outlined"
                            fullWidth
                            value={formData.email}
                            onChange={handleChange}
                            error={!!errors.email}
                            helperText={errors.email}
                            InputLabelProps={{ style: { color: 'lightgray' } }}
                            InputProps={{ style: { color: 'white', borderRadius: "12px" } }}
                            sx={{
                                "& .MuiOutlinedInput-root": {
                                    "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                    "&:hover fieldset": { borderColor: "white" },
                                    "&.Mui-focused fieldset": {
                                        borderColor: "#a29bfe",
                                        boxShadow: "0 0 8px #a29bfe"
                                    },
                                },
                            }}
                        />

                        <TextField
                            label="Message"
                            name="message"
                            multiline
                            rows={4}
                            variant="outlined"
                            fullWidth
                            value={formData.message}
                            onChange={handleChange}
                            error={!!errors.message}
                            helperText={errors.message}
                            InputLabelProps={{ style: { color: 'lightgray' } }}
                            InputProps={{ style: { color: 'white', borderRadius: "12px" } }}
                            sx={{
                                "& .MuiOutlinedInput-root": {
                                    "& fieldset": { borderColor: "rgba(255,255,255,0.3)" },
                                    "&:hover fieldset": { borderColor: "white" },
                                    "&.Mui-focused fieldset": {
                                        borderColor: "#a29bfe",
                                        boxShadow: "0 0 8px #a29bfe"
                                    },
                                },
                            }}
                        />

                        <Button
                            type="submit"
                            variant="contained"
                            sx={{
                                background: "linear-gradient(135deg, #ffffff, #dfe6e9)",
                                color: 'rgb(13, 1, 33)',
                                "&:hover": {
                                    background: "linear-gradient(135deg, #4a2a87, #6c5ce7)",
                                    color: "white"
                                },
                                fontSize: "1rem",
                                textTransform: "none",
                                fontWeight: "bold",
                                borderRadius: "30px",
                                py: 1.5,
                                transition: "0.3s ease",
                            }}
                        >
                            Submit
                        </Button>
                    </Box>
                </Grid>

                {/* Right Column: Contact Details */}
                <Grid item xs={12} md={6}>
                    <Box sx={{ textAlign: { xs: 'center', md: 'left' }, px: { xs: 2, md: 4 } }}>
                        <Typography
                            variant="h4"
                            sx={{
                                fontWeight: 600,
                                mb: 3,
                                color: 'yellow',
                            }}
                        >
                            Get In Touch
                        </Typography>

                        <Typography variant="body1" sx={{ mb: 2, fontSize: "1.1rem" }}>
                            üìß Email: <a href="mailto:youremail@gmail.com" style={{ color: 'lightgray', textDecoration: 'none' }}>youremail@gmail.com</a>
                        </Typography>

                        <Typography variant="body1" sx={{ mb: 2, fontSize: "1.1rem" }}>
                            üì± Phone: <a href="tel:+911234567890" style={{ color: 'lightgray', textDecoration: 'none' }}>+91 12345 67890</a>
                        </Typography>

                        <Typography variant="body1" sx={{ fontSize: "1.1rem" }}>
                            üìç Location: Bangalore, India
                        </Typography>
                    </Box>
                </Grid>
            </Box>
        </Grid>
    );
};

export default Contact;
