import { Grid, Typography, Box, Card } from '@mui/material';
import React from 'react';
import ui from '../../assets/ui-design.png';
import web from '../../assets/website-design.png';
import graphic from '../../assets/app-design.png';

const skillsData = [
  {
    id: 1,
    title: "UI/UX design",
    img: ui,
    desc: "I create user-friendly interfaces that are visually appealing and improve user experience. For smooth interactions, I concentrate on striking a balance between creativity and functionality",
  },
  {
    id: 2,
    title: "Website design",
    img: web,
    desc: "I create cutting-edge, responsive, and effective websites.Converting concepts into useful digital solutions is my area of competence.",
  },
  {
    id: 3,
    title: "Google ads",
    img: graphic,
    desc: "I create targeted ad campaigns that maximize reach and conversions.With data- driven strategies, I help businesses achieve measurable growth.",
  },
  {
    id: 4,
    title: "SEO (Search Engine Optimization)",
    img: graphic,
    desc: "I create targeted ad campaigns that maximize reach and conversions.With data- driven strategies, I help businesses achieve measurable growth.",
  },
];

const Skills = () => {
  return (
    <Grid
      container
      sx={{
        position: 'relative',
        backgroundColor: 'rgb(30, 30, 30)',
        px: { md: 20, xs: 3 },
        py: { md: 6, xs: 4 },
        mt: { md: -8, xs: 3 },
        minHeight: { xs: '100vh', md: '90vh' },
        overflow: 'hidden',
        justifyContent: 'center',
      }}
    >
      <Box sx={{ width: '100%' }}>
        {/* Heading */}
        <Typography
          variant="h2"
          sx={{
            color: 'white',
            fontWeight: 600,
            fontFamily: 'inherit',
            fontSize: { md: '3rem', xs: '2rem' },
            textAlign: 'center',
          }}
        >
          What I Do
        </Typography>

        <Typography
          variant="body2"
          sx={{
            color: 'lightgray',
            fontFamily: 'inherit',
            fontSize: { md: '1.2rem', xs: '0.9rem' },
            lineHeight: 1.5,
            textAlign: 'center',
            mt: 2,
            mb: 5,
          }}
        >
          I am a skilled and passionate web designer with experience in
          creating visually appReact.jsnd user-friendly websites. I have a strong
          understanding of design and a keen eye for detail. I am proficient in HTML,
          CSS, JavaScript, and React.js, as well as design software such as Adobe Photoshop.
        </Typography>

        {/* Skills List Style */}
        <Grid container spacing={3} justifyContent="center">
          {skillsData.map((skill) => (
            <Grid item xs={12} md={6} key={skill.id}>
              <Card
                sx={{
                  background: 'lightgray',
                  color: '#1e1e1e',
                  height: '25vh',
                  borderRadius: 2,
                  boxShadow: 3,
                  p: 2,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 2,
                  "&:hover": { transform: 'translateY(-5px)', boxShadow: 8 },
                }}
              >
                {/* Icon */}
                <Box
                  component="img"
                  src={skill.img}
                  alt={skill.title}
                  sx={{
                    width: 50,
                    height: 50,
                    objectFit: 'contain',
                  }}
                />

                {/* Title + Description */}
                <Box>
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: 600,
                      fontSize: { md: "1.2rem", xs: "1rem" },
                    }}
                  >
                    {skill.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    sx={{
                      color: "black",
                      fontSize: { md: "1rem", xs: "0.85rem" },
                    }}
                  >
                    {skill.desc}
                  </Typography>
                </Box>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Grid>
  );
};

export default Skills;
