import React, { useState } from 'react';
import { Grid, Card, CardContent, Typography, CardMedia } from '@mui/material';
import princy from '../assets/cards image/princy.png';
import image2 from '../assets/cards image/princy.png';
import image3 from '../assets/cards image/princy.png';

const imageList = [princy, image2, image3]; // Array of image sources
const names = ["Ramanuj", "Shankari", "Kumar"]; // Array of names
const sname=['Principal, Siddaganga School', 'Principal, Siddaganga PU College', 'Principal, Siddaganga College of Management & Science']
const messages = [
  "Ramanuj believes in empowering students with the right knowledge and skills to thrive in a digital world.",
  "Shankari emphasizes holistic development and encourages students to embrace technology creatively.",
  "Kumar advocates for innovation and lifelong learning to succeed in a competitive landscape."
]; // Array of unique messages

const Cards = () => {
  const [activeCard, setActiveCard] = useState(null);

  const toggleCard = (index) => {
    setActiveCard(activeCard === index ? null : index);
  };

  return (
    <Grid
      container
      spacing={3}
      sx={{
        background: 'linear-gradient(to top,#ffffff, #D9EEE1)',
        padding: { xs: 3, sm: 4, md: 6 },
        height: '100%',
      }}
    >
      <Grid item xs={12}>
        <Typography
          variant="h4"
          align="center"
          sx={{ fontWeight: 600, fontSize: { xs: '1.5rem', sm: '2rem', md: '2.5rem' } }}
        >
          Principal Message
        </Typography>
      </Grid>
      {imageList.map((image, index) => (
        <Grid item xs={12} sm={6} md={4} key={index}>
          <Card
            sx={{
              background: '#907394',
              height: '300px', // Fixed height
              cursor: 'pointer',
            }}
            onClick={() => toggleCard(index)}
          >
            <CardMedia>
              <img
                src={image}
                alt={`Image ${index + 1}`}
                style={{
                  height: '200px',
                  transition: 'all 0.9s ease-in-out',
                  width: activeCard === index ? '100%' : '98%', 
                  objectFit: 'cover',
                  margin: 'auto',
                }}
              />
            </CardMedia>
            <CardContent>
              <Typography
                variant="h5"
                align="left"
                sx={{
                  fontWeight: 600,
                  fontSize: { xs: '1rem', sm: '1.25rem' },
                  // marginTop: ,
                  mb:1,
                }}
              >
                {names[index]}
              </Typography>
              <Typography> {sname[index]} </Typography>
            </CardContent>
          </Card>

          {/* Additional Content - Visible on Click */}
          {activeCard === index && (
            <Card
              sx={{
                backgroundColor: 'lightgreen',
                marginTop: 3,
                width: '100%',
                minHeight: '100px', // Ensure fixed minimum height for additional content
              }}
            >
              <CardContent>
                <Typography
                  variant="body1"
                  sx={{
                    fontSize: { xs: '1rem', sm: '1rem', md: '1.1rem' },
                    textAlign: { xs: 'left', sm: 'left' },
                  }}
                >
                  {messages[index]}
                </Typography>

              </CardContent>
            </Card>
          )}
        </Grid>
      ))}
    </Grid>
  );
};

export default Cards;
