import React from 'react';
import { Grid, Card, CardContent, Typography } from '@mui/material';
import princy from '../assets/cards image/princy.png';
import image2 from '../assets/cards image/princy.png'; // Add more image imports
import image3 from '../assets/cards image/princy.png';

const imageList = [princy, image2, image3]; // Array of image sources
const name=["Ramanuj", "Shankari", 'Kumar']
const Cards = () => {
  return (
    <Grid
      container
      spacing={2}
      sx={{ background: 'linear-gradient(to top,#ffffff, #D9EEE1)', height: '50vh', padding: 2 , }}
    >
      {imageList.map((image, index) => (
        <Grid item xs={12} sm={4} key={index}>
          <Card
            sx={{
              background: 'yellow',
              height: '100%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <img
              src={image}
              alt={`Image ${index + 1}`}
              style={{ height: '100%', width: '100%', objectFit: 'cover' }}
            />
          </Card>
          <CardContent>
            <Typography> Name: {index.name}</Typography>
          </CardContent>

        </Grid>
      ))}
    </Grid>
  );
};

export default Cards;
