import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner2 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import banner3 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; 
import KeyboardDoubleArrowLeftIcon from '@mui/icons-material/KeyboardDoubleArrowLeft';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';

const CustomArrow = ({ direction, onClick }) => {
    return (
      <div
        onClick={onClick}
        style={{
          display: 'block',
          zIndex: 3,
          backgroundColor: 'blue', // Semi-transparent background
          borderRadius: '50%', // Circular button
          padding: '10px',
          cursor: 'pointer', // Pointer on hover
        //   transition: 'transform 0.3s ease', // Smooth animation
          transform: 'scale(0)', // Initial scale
          position: 'absolute',

          top: '50%', // Center vertically
          transform: 'translateY(-50%)', // Adjust for centering
          right: direction === 'next' ? '30px' : 'unset', // Position for the right arrow
          left: direction === 'prev' ? '30px' : 'unset', // Position for the left arrow
        }}
      >
        {/* Add arrow icon directly */}
        <span
          style={{
            fontSize: '24px',
            color: 'white',
            userSelect: 'none',
          }}
        >
          {direction === 'next' ? <KeyboardDoubleArrowRightIcon/>:<KeyboardDoubleArrowLeftIcon/> }
        </span>
      </div>
    );
  };


const Banner = () => {
  // Slider settings
  const settings = {
    dots: false,
    infinite: true,
    speed: 500, // Smooth transition speed
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrow direction="next" />,
    prevArrow: <CustomArrow direction="prev" />,
  };

  // Array of images
  const images = [banner1, banner2, banner3];

  return (
    <div style={{ width: '100%' }}>
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index}>
            <img
              src={image}
            //   alt={Banner ${index + 1}}
              style={{
                width: '100%',
                height: 'auto', // Ensures full image is displayed
                maxHeight: '600px', // Limit height for consistent appearance
                objectFit: 'cover', // Fills the screen width
              }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};



export default Banner;
