import React from 'react';
import { Grid, Container, Box, Typography } from '@mui/material';
import portfolio1 from '../../assets/reactjs.png'; // Replace with actual image paths
import portfolio2 from '../../assets/html.png';
import portfolio3 from '../../assets/javascript.png';

const Experince = () => {
    const stats = [
        {
            count: 1.5,
            label: 'Year of Experience',
            img: portfolio1,
        },
        {
            count: 10,
            label: 'Completed Projects',
            img: portfolio2,
        },
        {
            count: 5,
            label: 'Expert Employees',
            img: portfolio3,
        },
    ];

    const scrollImages = {
        display: 'flex',
        animation: 'scrollImages 10s linear infinite',
    };

    const imgStyle = {
        display: 'inline-block',
        marginRight: '20px',
        width: '200px',
        height: 'auto',
    };

    const imgContainerStyle = {
        width: '100%',
        height: 'auto',
        borderRadius: '8px',
    };

    const textStyle = {
        color: 'white',
        fontSize: '0.8rem',
        textAlign: 'center',
        marginTop: '10px',
    };

    const keyframesStyle = {
        '@keyframes scrollImages': {
            '0%': {
                transform: 'translateX(0)',
            },
            '100%': {
                transform: 'translateX(-100%)', // Adjust depending on the width
            },
        },
    };

    return (
        <Container
            maxWidth={false}
            sx={{
                backgroundColor: '#fff',
                py: { xs: 2, md: 2 },
                px: 2,
                width: { md: '95vw', sm: '85vw', xs: '90vw' },
                borderRadius: 2,
                overflow: 'hidden', // To hide overflowed images
            }}
        >
            <Grid
                container
                spacing={2} // Increased spacing between items
                justifyContent="center"
                alignItems="center"

            >
                <Box sx={{ width: '100%' }}>
                    {/* Heading */}
                    <Typography
                        variant="h2"
                        sx={{
                            color: 'white',
                            fontWeight: 600,
                            fontFamily: 'inherit',
                            fontSize: { md: '3rem', xs: '2rem' },
                            textAlign: 'center',
                        }}
                    >
                        What I Do
                    </Typography>

                    <Typography
                        variant="body2"
                        sx={{
                            color: 'lightgray',
                            fontFamily: 'inherit',
                            fontSize: { md: '1.2rem', sm: '1.3rem', xs: '0.9rem' },
                            lineHeight: 2,
                            textAlign: 'center',
                            mt: 2,
                            mb: 5,

                        }}
                    >
                        I am a skilled and passionate web designer with experience in creating visually appealing and user-friendly websites. I have a strong understanding of design and a keen eye for detail. I am proficient in HTML, CSS, JavaScript, and React.js. Along with my technical expertise, I also have knowledge of digital marketing, including SEO, Google Ads, and content strategies, which helps me create websites that not only look great but also perform effectively in reaching the right audience.
                    </Typography>
                </Box>
                <Grid item xs={12} sx={{ overflow: 'hidden' }}>
                    {/* Inline styles for auto-scrolling image container */}
                    <Box sx={{ ...scrollImages, ...keyframesStyle }}>
                        {stats.map((stat, index) => (
                            <Box key={index} sx={imgStyle}>
                                <Box
                                    component="img"
                                    src={stat.img}
                                    alt={`portfolio ${index}`}
                                    sx={imgContainerStyle}
                                />
                            </Box>
                        ))}
                    </Box>
                </Grid>

            </Grid>
        </Container>
    );
};

export default Experince;
